import { StatusBar } from 'expo-status-bar';
import React,{ useState ,useEffect,useContext} from 'react';
import { Animated,ImageBackground,StyleSheet, Text, View ,Image} from 'react-native';
import {LinearGradient} from 'expo-linear-gradient';
import {Context as AuthContext} from '../context/AuthContext';
import {
  widthPercentageToDP as wp,
  heightPercentageToDP as hp,
  listenOrientationChange as loc,
  removeOrientationListener as rol
} from 'react-native-responsive-screen';
import { ScaledSheet } from 'react-native-size-matters';
import { s, vs, ms, mvs } from 'react-native-size-matters';
import Svg, { G, Defs, ClipPath, Rect, Stop, Line,Polyline, Animate, Polygon } from 'react-native-svg';
console.disableYellowBox = true;


//import Animated, { Easing } from 'react-native-reanimated';

const Progress=({step, steps, height,colorb}) =>{

    const animatedValue=React.useRef(new Animated.Value(-1000)).current;
    const reactive=React.useRef(new Animated.Value(-1000)).current;
    const [width, setWidth]=React.useState(0);
    React.useEffect(() =>{
        Animated.timing(animatedValue, {
          toValue:reactive,
          duration:500,
          //easing:Easing.bounce(),
          useNativeDriver: true,
        }).start();
    }, []);
    React.useEffect(() => {
        reactive.setValue(-width +(width*step)/steps+(width*8)/steps);
    }, [step, width]);
    
    return ( 
    <View style={{margin: wp("1.5%") }}> 
      <View style={styles.txt}>
        <Text  style={styles.text}>{step}°C</Text>
      </View>
      <View 
        onLayout={(e) =>{
          const newWidth =e.nativeEvent.layout.width;
          setWidth(newWidth);
        }}
        style={[styles.containerr1, { height, borderRadius: height,}]}
      >
        <Animated.View  
          style={[styles.container2, {
            borderRadius: height,
            height,
            transform:[{translateX:animatedValue,  /*animatedValue*/ },],
          }]}
        >
          <LinearGradient  /*Animated.View*/ colors={colorb}>
            <Text style={styles.txttt}></Text>
          </LinearGradient>
        </Animated.View>
      </View>
    </View>
  )
}

const ProgressBar=(props) => {
  const {state,signin} =useContext(AuthContext);

  return (
      <View style={styles.container}>
        <Image
          style={styles.barsImage}
          source={require('../images/bars.png')}
        />
        <View style={styles.transform} >
          <StatusBar hidden />
          <Progress 
            step={props.dataMin}  steps={67} 
            height={wp('15%')} colorb={['#27AAE1','#F7941D']}
          />
          <Text></Text>
          <Progress 
            step={props.dataMoy}  steps={67} 
            height={wp('15%')} colorb={['#ffc500','#F15A29']}
          />
          <Text></Text>
          <Progress 
            step={props.dataMax}  steps={67} 
            height={wp('15%')} colorb={['#F15A29','#BE1E2D']}
          />
        </View>
      </View>
  );
};

export default ProgressBar;

const styles = ScaledSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    marginTop: hp('-16%')
  },

  barsImage:{
      zIndex: 3,
      position:'absolute',
      marginLeft: wp("10%"),
      width: wp("66%"),
      height: hp("44%")
  },

  transform:{
    transform: [
      { rotate: "-90deg" }, 
    ],
    marginTop: hp('6%'),
    marginLeft: wp("2%")
  },

  txt:{
    marginTop: '72@vs',
    marginLeft: '320@s',
    position:'absolute',
    zIndex:2
  },

  text:{
    transform: [
      { rotate: "90deg" }, 
    ],
    fontSize: '14.5@s',
    fontWeight:'bold',
    color:'transparent'
  },

  containerr:{
    flex: 1,
    height: hp('60%'),
  },
 
  containerr1:{
    zIndex:1,
    position:'relative',
    width: wp("85%"),
    marginLeft: wp("-3%"), //-65 
    backgroundColor:'#EDECEC',
    overflow:"hidden",
  },

  container2:{
    width: wp('100%'),
    backgroundColor: 'transparent',
    borderTopRightRadius:0,
    borderBottomRightRadius:0,
    //position:'absolute',
    left: '0@s',
    top: '0@vs',
    right: '3@s',
  },

  txttt:{
    height: '70@vs',
    marginRight: wp("0%")
  }
});
