import React, {Component,useState,useContext,useEffect} from 'react';
import {
  View,
  Dimensions, 
  Modal, 
  Text,
  Pressable,
  Linking,
  ScrollView,
  Image,
  StyleSheet,
  TouchableOpacity
} from 'react-native';
import {
  widthPercentageToDP as wp,
  heightPercentageToDP as hp,
  listenOrientationChange as loc,
  removeOrientationListener as rol
} from 'react-native-responsive-screen';
import { ScaledSheet } from 'react-native-size-matters';
import {Context as AuthContext} from '../context/AuthContext';

const  Modell=(props) =>  {
    const [modalVisible, setModalVisible] = useState(false);
    const {state,signin} =useContext(AuthContext);
    const [currentDate, setCurrentDate] = useState('');
    const [currentMonth, setCurrentMonth] = useState('');
    useEffect(() => {
      setCurrentDate(
          new Date().getDate()
      );
      setCurrentMonth(
          new Date().getMonth()+1
      );    
    }, []);
    return(
      <View>
        <Pressable
          style={{width: '61@s'}}
          onPress={() => setModalVisible(true)}
        >
          <Image source={require('../images/not.png')} style={styles.image2} />
        </Pressable>
        <View style={styles.centeredView}>
          <Modal
            animationType="slide"
            transparent={true}
            visible={modalVisible}
            onRequestClose={() => {
              Alert.alert("Modal has been closed.");
              setModalVisible(!modalVisible);
            }} 
          >
          <View style={styles.centeredView}>
            <View style={styles.modalView}>
              <Pressable
                style={{marginLeft: wp("113%"),marginTop: hp("-12%")}}
                onPress={() => setModalVisible(!modalVisible)}
              >
                <Image source={require('../images/close.png')} /> 
              </Pressable>
              <View>
                <Text style={{fontWeight:'bold',marginBottom: hp("8%"),color:'#2C5364',marginTop: hp("5%"), fontSize: '20@s'}}>{state.reponse['device infomation'].station}  {state.reponse['data infomation']['j'].Date}</Text>
              </View>
              <View style={styles.itemView2}>
                <View style={{marginRight: wp("35%"),flexDirection:'row'}}>
                  <View>
                    <Image source={require('../images/soliel.png')}/>
                  </View>
                  <View style={{marginLeft: wp("10%")}}>
                    <Text style={{fontWeight:'bold',color:'#00B4DB'}}>{state.reponse['data infomation']['j-1'].Tmin+"°C"}</Text>
                    <Text style={{fontWeight:'bold',color:'#ED213A'}}>{state.reponse['data infomation']['j-1'].Tmax+"°C"}</Text>
                  </View>
                </View>
                <View>
                  <Image source={require('../images/precipitation.png')}/>
                  <Text style={{fontWeight:'bold',color:'#00BBEA'}}>{state.reponse['data infomation']['j-1'].Pr ? ['data infomation']['j'].Pr+"mm" :"0 mm"}</Text>
                </View>
              </View>
              <View style={styles.itemView2} >
                <View style={{marginRight: wp("39%"),flexDirection:'row',marginLeft: wp("12%")}}>
                  <View>
                    <Image source={require('../images/vent1.png')}/>
                  </View>
                  <View >
                    <Text style={{fontWeight:'bold',color:'#75CEDE'}}>{state.reponse['data infomation']['j-1'].Hrmin+"%"}</Text>
                    <Text style={{fontWeight:'bold',color:'#3D57A7'}}>{state.reponse['data infomation']['j-1'].Hrmax+"%"}</Text>
                  </View>
                </View>
                <View >
                  <Image source={require('../images/song.png')} style={{marginLeft: wp("18%")}}/>
                  <Text style={{fontWeight:'bold',color:'#FFC906',marginRight: wp("0%")}} >{state.reponse['data infomation']['j-1'].Rg+"J/cm²"}</Text>
                </View>
              </View>
              <View style={styles.itemView2} >
                <View style={{marginLeft: wp("7%")}}>
                  <Image source={require('../images/rayonnement.png')}/>
                  <Text style={{fontWeight:'bold',color:'#87BE3F'}}>{state.reponse['data infomation']['j-1'].Et0+"mm"}</Text>
                </View>
                <View style={{marginLeft: wp("53%")}}>
                  <Image   source={require('../images/vent.png')}/>
                  <Text style={{fontWeight:'bold',color:"#E35264",marginLeft: wp("4%")}}>{state.reponse['data infomation']['j-1'].Vvent+"Km/h"}</Text>
                </View>
              </View> 
            </View> 
          </View>
          </Modal>
        </View>
      </View>
    )
  }

export default Modell;

const styles = StyleSheet.create({
    modalView: {
      margin: 20,
      backgroundColor: "white",
      borderRadius: 20,
      padding: 35,
      width:290,
      alignItems: "center",
      shadowColor: "#000",
      shadowOffset: {
        width: 0,
        height: 2
      },
      shadowOpacity: 0.25,
      shadowRadius: 4,
      elevation: 5
    },

    itemView2: {
      flexDirection: 'row',
      marginBottom:"4%"
    },

    centeredView: {
      flex: 1,
      justifyContent: "center",
      alignItems: "center",
      marginTop: 15,
    },

    image2:{
      marginLeft:"9%",
      marginTop:"-4%"
    }
  })
