import React,{Component, useState, useContext, useEffect} from 'react';
import { StatusBar } from 'expo-status-bar';
import { 
    View, 
    Text, 
    TouchableOpacity, 
    TextInput,
    Platform,
    StyleSheet ,
    Dimensions,
    Alert,
    Image,
    Linking,
    BackHandler,
    KeyboardAvoidingView,
    TouchableWithoutFeedback,
    Keyboard
} from 'react-native';
import {
    widthPercentageToDP as wp,
    heightPercentageToDP as hp,
    listenOrientationChange as loc,
    removeOrientationListener as rol
} from 'react-native-responsive-screen';
import { ScaledSheet } from 'react-native-size-matters';
import { s, vs, ms, mvs } from 'react-native-size-matters';
import {Context as AuthContext} from '../context/AuthContext';
//import {Context1 as Alldevice} from '../context/Alldevice';
import * as Animatable from 'react-native-animatable';
import {Card} from 'react-native-shadow-cards';
import Footer from './Footer';
import {LinearGradient} from 'expo-linear-gradient';
import FontAwesome from 'react-native-vector-icons/FontAwesome';
import MaterialIcons from 'react-native-vector-icons/MaterialIcons';
import Feather from 'react-native-vector-icons/Feather';
import Langue2 from './Langue2';
import { set } from 'react-native-reanimated';
import AuthentificationAPI from '../API/AuthentificationAPI';
import {getDATAFromApi} from '../API/DATAAPI';
import { ScrollView } from 'react-native-gesture-handler';
import  Dashboard  from './Dashboard';
import * as Device from 'expo-device';


console.disableYellowBox = true;

const Login=(props) => {

    const handleWebPress =() => {
        Linking.openURL("http://www.yobeen.com");
    }
    const[results,setResults]=useState([]);
    const {state,signin} =useContext(AuthContext);
    const [data, setData] =useState({
        username: '',
        password: '',
        check_textInputChange: false,
        secureTextEntry: true,
        isValidUser: true,
        isValidPassword: true,
    });
    const handlePasswordChange = (val) => {
        if( val.trim().length >= 8 ) {
            setData({
                ...data,
                password: val,
                isValidPassword: true,
            });
        } else {
            setData({
                ...data,
                password: val,
                isValidPassword: false
            });
        }
    };
    const updateSecureTextEntry = () => {
        setData({
            ...data,
            secureTextEntry: !data.secureTextEntry
        });
    };
    const textInputChange = (val) => {
        if( val.trim().length == 10  ) {
            setData({
                ...data,
                username: val,
                check_textInputChange: true,
            });
        } else {
            setData({
                ...data,
                username: val,
                check_textInputChange: false,
            });
        }
    };
 
    const erreur1=() =>{
        if (state.reponse['user infomation'].length==0) {
            Alert.alert('OOPS!','Ce numéro de téléphone n\'est pas valide',[
                {  text:'OK',onPress:()=>console.log('alert closed')}
            ])
        };
    };
    const erreur2=() =>{
        if(state.reponse.message_info=="The phone number is not valid"){
            Alert.alert('OOPS!','Veuillez saisir un numéro de téléphone de 10 chiffres',[
                {text:'OK',onPress:()=>console.log('alert closed')}
            ])
        } else{
            erreur1()
        }
    }; 

    const toNavigate = () => {
        signin(data.username);
    };  

    const deviceInfos = {
        brand: Device.brand,
        modelName: Device.modelName,
        id: Device.osBuildId,
    };
   

    return (
    <TouchableWithoutFeedback onPress={Keyboard.dismiss}>
        <KeyboardAvoidingView style={{flex:1, backgroundColor: '#FFF'}} >
            <View style={styles.view1}>
                <Image
                    style={styles.header}
                    source={require('../images/green.png')}
                />
                <Langue2 />  
            </View>
            <View style={styles.view2}>
                <LinearGradient
                    colors={['#fff', '#fff']}
                    style={[styles.signIn, {
                        borderColor: '#7FD0D7',
                        borderWidth: 2,
                        borderRadius: 50,
                        marginBottom: hp("1%")
                    }]}
                >
                    <View style={styles.action}>
                        <Image
                            style={{marginLeft: wp("5%")}}
                            source={require('../images/tlphn.png')}
                        />
                        <TextInput 
                            placeholder="Entrez votre numéro de téléphone"
                            placeholderTextColor="#7FD0D7"
                            style={styles.textInput}
                            autoCapitalize="none"
                            keyboardType="numeric"
                            underlineColorAndroid="transparent"
                            value={data.username}
                            onChangeText={(val) => textInputChange(val)}
                        />
                        { data.check_textInputChange ? 
                            <Image style={{marginRight: wp("6%"),width: wp("6.5%"),height: hp("4%")}} source={require('../images/verifier.png')}/>
                            :
                            null
                        }
                    </View>
                </LinearGradient>
                { state.reponse.message_info ? <Text style={styles.errorMsg}>Ce numéro de téléphone est invalide</Text> : <Text></Text> }
                <TouchableOpacity
                    onChange={() =>state.reponse.message_info=='' }
                    onPress={toNavigate}
                    style={styles.signInn}
                    >
                    <LinearGradient
                        colors={['#118DCD', '#118DCD']}
                        style={styles.signIn}
                    >
                        <Text style={[styles.textSign, {color:'#fff'}]}>Se connecter</Text>
                    </LinearGradient>
                </TouchableOpacity>
            </View>
            <View>        
                <View>
                    <Image
                        style={styles.ciel}
                        source={require('../images/ciel.png')}
                        resizeMode="stretch"
                    />
                </View>
                <Footer/>
            </View> 
        </KeyboardAvoidingView>
    </TouchableWithoutFeedback>
    );
};

export default Login;

const {height} = Dimensions.get("screen");

const styles = ScaledSheet.create({
    borderStyleBase: {
        width: 30,
        height: 45
      },
     
      borderStyleHighLighted: {
        borderColor: "#03DAC6",
      },
     
      underlineStyleBase: {
        width: 30,
        height: 45,
        borderWidth: 0,
        borderBottomWidth: 1,
      },
     
      underlineStyleHighLighted: {
        borderColor: "#03DAC6",
      },

    view1:{
        flex: 1,
        alignItems: 'flex-end' ,
        justifyContent: 'flex-start' ,
    } ,
    
    view2: {
        flex: 1, 
        backgroundColor: '#fff',
        marginTop:hp("12%"),
        //alignItems:'center',
        //marginTop:30,
        height,
        alignItems:'center',
        alignContent:'center',
    },

    header: {
        width: wp("100%"),
        height: hp("40%"),
        marginRight: wp("-5%"),
        marginTop: hp("-2%")
    },
    
    ciel:{
        marginBottom: hp("-7%"),
        marginRight: wp("8%"), 
        backgroundColor:'transparent'
    },
      
    action: {
        flexDirection: 'row',
        marginTop: hp('1.5%'),
        borderBottomWidth: 1,
        borderBottomColor: '#f2f2f2',
        paddingBottom: hp("1%"),
    },

    textInput: {
        flex: 1,
        marginTop: hp("0%"),
        paddingLeft: 10,
        color: '#05375a',
        fontSize: '13@s'
        //borderBottomColor: 'transparent',
    },

    errorMsg: {
        color: '#FF0000',
        fontSize: '14@s',
        marginBottom: hp("2%"),
        fontWeight:'bold'
    },

    signIn:{
        width: wp('80%'),
        height: hp("7%"),
        justifyContent: 'center',
        alignItems: 'center',
        borderRadius: 10,
        marginTop: hp("2%")
    },

    buttonGet:{
        width: wp('20%'),
        height: hp("6%"),
        justifyContent: 'center',
        alignItems: 'center',
        borderRadius: 10,
        marginTop: hp("3%")
    },

    get: {
        justifyContent: 'center',
        alignItems: 'flex-start',
        borderRadius: 10,
        marginLeft: wp("6%")
    },

    textGet: {
        fontSize: '14@s',
        fontWeight: 'bold'
    },

    actionGet: {
        marginLeft: wp('-1%'),
        borderBottomWidth: 1,
        borderBottomColor: '#f2f2f2',
    },

    inputImei: {
        color: '#05375a',
        fontSize: '15@s'
    },

    signInGet:{
        width: wp('60%'),
        height: hp("6%"),
        justifyContent: 'center',
        alignItems: 'center',
        borderRadius: 10,
        marginTop: hp("3%"), 
        marginLeft: wp('2%'),
    },

    signInn: {
        width: wp('100%'),
        height: hp("7%"),
        justifyContent: 'center',
        alignItems: 'center',
        borderRadius: 10,
        marginBottom: hp("2%")
    },

    textSign: {
        fontSize: '18@s',
        fontWeight: 'bold'
    },
   
});
