import React,{useContext,useState,useEffect} from 'react'
import { View, Text,  StyleSheet, ScrollView, ImageBackground,Image,Dimensions} from 'react-native';
import Search from './Search';
import Menu1 from './Menu1'
import Pluie from './Pluie';
import Header from './Header';
import Footer from './Footer';
import Base from './Base'
console.disableYellowBox = true;
import {Context as AuthContext} from '../context/AuthContext';
import DateA from './DateA' ;
import {LinearGradient} from 'expo-linear-gradient';
import ButtomVoir from './ButtomVoir'
import { TouchableOpacity } from 'react-native-gesture-handler';

const precipitation=(props) =>{
  const {width, height} = Dimensions.get('window')
  const {state,signin} =useContext(AuthContext);
  const [currentDate, setCurrentDate] = useState('');
  const [currentMonth, setCurrentMonth] = useState('');
  useEffect(() => {
    console.log(props.navigation.state.params.indexx)
      setCurrentDate(
        new Date().getDate()
      );
      setCurrentMonth(
        new Date().getMonth()+1
      );
      alldevice(props.navigation.state.params.indexx)  
      console.log(props.navigation.state.params.indexx)  
    }, []);
    const alldevice=(a)=>{
      if (a!=1000){ 
        setStationn({
          nom:state.reponse['devices'][a]['station'],
        })  
        setData({
          dateJ:state.reponse['data infomation']['j'].Date,
          Pr:state.reponse['devices'][a]['data'][0]['j'].Pr,
        })
      }
    }
    const[ stationn,setStationn]=useState({
        nom:state.reponse['device infomation'].station,
    });
    const [data, setData] = React.useState({
      dateJ:state.reponse['data infomation']['j'].Date,
      Pr: state.reponse['data infomation']['j'].Pr,
    });
    const [date, setDate] = React.useState({
      picture:require('../images/j1.png'),
    });  
    
    return (
      <View style={{flex:1}}>
        <View>
          <Image  source={require('../images/ArrierePr.png')} style={styles.actionn}/>
          <Header onPress={()=>props.navigation.navigate('Dashboard')}/>
        </View>
        <ScrollView  style={styles.container}>
          <Image  source={require('../images/ArrierePr.png')} style={styles.action}/>
          <View style={styles.temView3}>
            <Text style={styles.temText1}>STATION{"\n"}{stationn.nom}</Text>  
            <Image source={require('../images/Position1.png')} style={styles.Image} />
          </View>
          <View style={styles.Cont}>
            <Text style={styles.Cont1}>{data.dateJ}</Text> 
            <Image source={require('../images/petit-nuage.png')} style={styles.ImageB} />
            <View>
              <Text style={styles.Cont2}>Précipitation</Text> 
              <Text style={styles.Cont3}>mm</Text>
            </View>
          </View>
          <View style={styles.temView2}>
            <View style={styles.temText5}>
              <View>
                <View style={{flex: 1}}>
                  <Image
                    style={{
                      alignItems:"center",
                      alignContent:"center",
                      marginLeft:"5%",
                    }}
                    source={date.picture}
                  />
                  <View style={{position: 'absolute',marginTop:"7%"}}>
                    <TouchableOpacity
                      onPress={() => {
                        if (props.navigation.state.params.indexx != 1000) {
                          setData({
                            Pr: state.reponse['devices'][props.navigation.state.params.indexx]['data'][0]['j-1'].Pr,
                            dateJ:state.reponse['data infomation']['j-1'].Date,
                          });
                        }
                        else {
                          setData({
                            dateJ:state.reponse['data infomation']['j-1'].Date,
                            Pr: state.reponse['data infomation']['j-1'].Pr,
                          });
                        };
                        setDate({
                          picture:require('../images/jm1.png')
                        })
                      }}
                    >
                      <DateA date2={(currentDate-1)+'\n'+'0'+currentMonth}/>
                    </TouchableOpacity>
                  </View>
                  <View style={{position: 'absolute',marginTop:"70%"}}>
                    <TouchableOpacity
                      onPress={() => {
                        if (props.navigation.state.params.indexx != 1000) {
                          setData({
                            Pr: state.reponse['devices'][props.navigation.state.params.indexx]['data'][0]['j'].Pr,
                            dateJ:state.reponse['data infomation']['j'].Date,
                          });
                        }
                        else {
                          setData({
                            dateJ:state.reponse['data infomation']['j'].Date,
                            Pr: state.reponse['data infomation']['j'].Pr,
                          });
                        };
                        setDate({
                          picture:require('../images/j1.png')
                        })
                      }}
                    >
                      <DateA date2={currentDate+'\n'+'0'+currentMonth}/>
                    </TouchableOpacity>
                  </View>
                  <View style={{position: 'absolute',marginTop:"130%"}}>
                    <TouchableOpacity
                      onPress={() => {
                        if (props.navigation.state.params.indexx != 1000) {
                          setData({
                            Pr: state.reponse['devices'][props.navigation.state.params.indexx]['data'][0]['j+1'].Pr,
                            dateJ:state.reponse['data infomation']['j+1'].Date,
                          });
                        }
                        else {
                          setData({
                            dateJ:state.reponse['data infomation']['j+1'].Date,
                            Pr: state.reponse['data infomation']['j+1'].Pr,
                          });
                        };
                        setDate({
                          picture:require('../images/jm2.png')
                        })
                      }}
                    >
                      <DateA date2={(currentDate+1)+'\n'+'0'+currentMonth}/>
                    </TouchableOpacity  >
                  </View>
                  <View style={{position: 'absolute',marginTop:"191%"}}>
                    <TouchableOpacity  
                      onPress={() => {
                        if (props.navigation.state.params.indexx != 1000) {
                          setData({
                            Pr: state.reponse['devices'][props.navigation.state.params.indexx]['data'][0]['j+2'].Pr,
                            dateJ:state.reponse['data infomation']['j+2'].Date,
                          });
                        }
                        else {
                          setData({
                            dateJ:state.reponse['data infomation']['j+2'].Date,
                            Pr: state.reponse['data infomation']['j+2'].Pr,
                          });
                        };
                        setDate({
                          picture:require('../images/jm3.png')
                        })
                      }}
                    >
                      <DateA date2={(currentDate+2)+'\n'+'0'+currentMonth}/>
                    </TouchableOpacity>
                  </View>
                </View>
              </View>
            </View>
            <View style={styles.temText6}>
              <View styles={{marginTop:12,marginLeft:"-50%"}}>
                <Pluie  picture={require('../images/nuage.png')} daata={data.Pr ? data.Pr+"mm" :"0 mm"}/>
              </View>
            </View>
          </View>
          <View style={styles.temText7}>
