import React, { useState ,useEffect,useContext} from 'react';
import ProgressBar1 from './ProgressBar1'
console.disableYellowBox = true;
import { View, Text,  StyleSheet, ScrollView,Image,TouchableOpacity, ImageBackground,Dimensions,ActivityIndicator} from 'react-native';
import Search from './Search';
import GraphVent from './GraphVent';
import Menu1 from './Menu1'
import {Context as AuthContext} from '../context/AuthContext';
import DateA from './DateA' ;
import Header from './Header';
import Footer from './Footer';
import ButtomVoir from './ButtomVoir'
import Compasss from "./Compasss" 
import {LinearGradient} from 'expo-linear-gradient';
import { VictoryScatter, VictoryChart,VictoryVoronoiContainer,VictoryAxis,VictoryArea,VictoryLabel,VictoryTheme } from "victory-native";
import {
  widthPercentageToDP as wp,
  heightPercentageToDP as hp,
  listenOrientationChange as loc,
  removeOrientationListener as rol
} from 'react-native-responsive-screen';
import { ScaledSheet } from 'react-native-size-matters';
import { s, vs, ms, mvs } from 'react-native-size-matters';


const VentGraph =(props)=> { 
 
  /*async componentDidMount() {
    await Font.loadAsync({
      'Poppins-BlackItalic': require('../assets/Fonts/Poppins-BlackItalic.ttf'),
      'OpenSans-Bold': require('../assets/Fonts/OpenSans-Bold.ttf'),
      'OpenSans-Regular': require('../assets/Fonts/OpenSans-Regular.ttf'),
      'adobe-arabe': require('../assets/Fonts/ArbFONTS-22016-adobearabic.ttf'),
      'Poppins-Bold': require('../assets/Fonts/Poppins-Bold.ttf'),
    });
    this.setState({ fontLoaded: true });
  }*/
  const {state,signin} =useContext(AuthContext);
  const [date, setDate] = React.useState({
    picture:require('../images/j1.png'),
  });  

  function float2int (value) {
    return value | 0;
  }
  const [currentDate, setCurrentDate] = useState('');
  const [currentMonth, setCurrentMonth] = useState('');
  useEffect(() => {
    setCurrentDate(
      new Date().getDate()
    );
    setCurrentMonth(
      new Date().getMonth()+1
    );      
  }, []);
  const [data, setData] = React.useState({
    dateJ:state.reponse['data infomation']['j'].Date,
    Vvent: state.reponse['data infomation']['j'].Vvent,
  });
  /*const [stateem1, setStateem1] = React.useState({
    img:require('../images/N.png'),
    value: state.reponse['data infomation']['j-1'].Dvent,
  });
  const [stateem2, setStateem2] = React.useState({
    img:require('../images/N.png'),
    value: state.reponse['data infomation']['j-2'].Dvent,
  });
  const [statee, setStatee] = React.useState({
    img:require('../images/N.png'),
    value: state.reponse['data infomation']['j'].Dvent,
  });
  const [statee1, setStatee1] = React.useState({
    img:require('../images/N.png'),
    value: state.reponse['data infomation']['j+1'].Dvent,
  });
  const [statee2, setStatee2] = React.useState({
    img:require('../images/N.png'),
    value: state.reponse['data infomation']['j+2'].Dvent,
  });
  const [statee3, setStatee3] = React.useState({
    img:require('../images/N.png'),
    value: state.reponse['data infomation']['j+3'].Dvent,
  });
  const [statee4, setStatee4] = React.useState({
    img:require('../images/N.png'),
    value: state.reponse['data infomation']['j+4'].Dvent,
  });
  const [statee5, setStatee5] = React.useState({
    img:require('../images/N.png'),
    value: state.reponse['data infomation']['j+5'].Dvent,
  }); */
  
    return ( 
      <View  style={styles.container}>
        <View style={styles.header}>
          <Image  source={require('../images/ArriereVen.png')} style={styles.headerImage}/>
          <Header onPress={()=>props.navigation.navigate('Vent')}/>
        </View>
        <ScrollView style={styles.scrollView}>
            <Image  source={require('../images/ArriereVen.png')} style={styles.imageBackground}/>
            <View style={styles.stationEtPosition}>
              <Text style={styles.stationText}>STATION{"\n"}{state.reponse['device infomation'].station}</Text>  
              <Image source={require('../images/Position1.png')} style={styles.positionImage} />
            </View>
            <View style={styles.dateEtVent}>
              <Text style={styles.date}>{data.dateJ}</Text> 
              <Image source={require('../images/Vent12.png')} style={styles.ventImage} />
              <View>
                <Text style={styles.ventText}>Le Vent</Text> 
                <Text style={styles.unit}>Km/h</Text>
              </View>
            </View>
            <GraphVent/>
        </ScrollView>
        <Footer/>
      </View>
    );
}

export default VentGraph;

const styles = ScaledSheet.create({
  container: {
    flex: 1,
  },
 
  header:{
  
  },

  headerImage: {
    height: hp('100%'),
    width: wp('100%'),
    position: 'absolute',
    //Swidth:240,
    alignContent: "center",
    //flex:1,
  },

  scrollView: {
    backgroundColor: '#fff'
  },

  scrollContainer:{
     
  },

  imageBackground:{
    height: hp("59%"),
    width: wp("100%"),
    paddingTop: hp("2%"),
    position: 'absolute',
    marginTop: hp("-25%")
  },

  stationEtPosition: {
    flexDirection: 'row',
  },

  stationText: {
    fontSize: '30@s',
    color: '#FFFFFF',
    marginTop: hp("3%"),
    marginBottom: hp("-19%"),
    margin: wp("2%"),
    marginLeft: wp("6%"),
    fontFamily: 'OpenSans-Bold', 
    fontWeight: 'bold'
  },

  positionImage: {
    marginLeft: wp("13%"),
    marginTop: hp("4%")
  },

  dateEtVent: {
    flexDirection: 'row',
    //alignItems:'center',
    backgroundColor: 'white',
    height: '110.42@vs',
    width: wp("90%"),
    margin: wp("5%"),
    borderRadius: 9.29,
    marginTop: hp("5%"),
    marginBottom: hp("5%"),
    marginRight: wp("5%"),
    marginLeft: wp("5%"),
    shadowColor: "#000",
    shadowOffset: {
      width: '0@s',
      height: '16@vs',
    },
    shadowOpacity: 0.58,
    shadowRadius: 16.00,
    elevation: 24, 
  },

  date: {
    marginTop: hp("5%"),
    marginLeft: wp("6%"),
    marginRight: wp("7%"),
    fontFamily: "Poppins-Regular",
    fontSize: '13.93@s',
    fontWeight: 'bold'
  },

  ventImage: { 
    marginLeft: wp("5%"),
    marginTop: hp("4%"),
    marginRight: wp("5%"),
    width: '75@s',
    height: '55@vs'
  },

  ventText: { 
    marginTop: hp("5%"),
    marginLeft: wp("7%"),
    fontWeight: 'bold',
    fontFamily: "Poppins-Regular",
    fontSize: '13.93@s',
  },

  unit: { 
    fontWeight: 'bold',
    marginLeft: wp("9%"),
    fontFamily: "Poppins-Regular",
    fontSize: '14@s',
  },

  graph:{
    width: wp("100%"),
    marginTop: hp("-5%")
  }
}
)
