import React,{useContext,useState,useEffect} from 'react'
import { View, Text,  StyleSheet, ScrollView,TouchableOpacity, ImageBackground,Image,Dimensions,ActivityIndicator} from 'react-native';
import Search from './Search';
import Menu1 from './Menu1'
import Dashboard from './Dashboard'
import Base from './Base';
import Header from './Header';
import Footer from './Footer';
import ButtomVoir from './ButtomVoir'
import Donut from './Donut';
import {Context as AuthContext} from '../context/AuthContext';
import DateA from './DateA' ;

import {LinearGradient} from 'expo-linear-gradient';

console.disableYellowBox = true;

const Humidite=(props)=> {
  
  const [date, setDate] = React.useState({
    picture:require('../images/j1.png'),

  
    
});  
 
  const {state,signin} =useContext(AuthContext);
  
  const [currentDate, setCurrentDate] = useState('');
  const [currentMonth, setCurrentMonth] = useState('');
  useEffect(() => {
    console.log(props.navigation.state.params.indexx)
      setCurrentDate(
          new Date().getDate()
      );
      setCurrentMonth(
          new Date().getMonth()+1
      );
    alldevice(props.navigation.state.params.indexx)  
    console.log(props.navigation.state.params.indexx)  
    }, []);
    const alldevice=(a)=>{
      if (a!=1000)
      { 
        setStationn({
          nom:state.reponse['devices'][a]['station'],
        })  
        setData({
          dateJ: state.reponse['data infomation']['j'].Date,

          Hrmoy:state.reponse['devices'][a]['data'][0]['j'].Hrmoy,
          Hrmin:state.reponse['devices'][a]['data'][0]['j'].Hrmin,
          Hrmax:state.reponse['devices'][a]['data'][0]['j'].Hrmax,
        })
      }

    }
    const[ stationn,setStationn]=useState(
      {
        nom:state.reponse['device infomation'].station,
       
      }
    );
    const [data, setData] = React.useState({
      dateJ: state.reponse['data infomation']['j'].Date,

      Hrmoy: state.reponse['data infomation']['j'].Hrmoy,
      Hrmin:  state.reponse['data infomation']['j'].Hrmin,
      Hrmax:  state.reponse['data infomation']['j'].Hrmax,
    
  }); 
  const index = props.navigation.state.params.indexx;

    return (
<View style={{flex:1}}>
  
      <ScrollView  style={styles.container2}>
                
        <View>
        <Image  source={require('../images/ArriereHu.png')} style={styles.action}>
         
         </Image>
         <Header
      onPress={()=>props.navigation.navigate('Dashboard')}
     />
   
           <View style={styles.temView3}>
           <Text style={styles.temText1}>STATION{"\n"}{stationn.nom}</Text>  
           <Image source={require('../images/Position1.png')} style={styles.Image} />
           </View>
         
      
      <View style={styles.Cont}>
                    <Text style={styles.Cont1}>{data.dateJ}</Text> 
                    <Image source={require('../images/humidité1.png')} style={styles.ImageB} />
<View>
                    <Text style={styles.Cont2}>Humidité relative
                    
                   
                    </Text> 
                     <Text style={styles.Cont3}>%</Text>
                     </View>
                    </View>
                   
     
    <View  >
  
      <View style={styles.temView2}>
        <View style={styles.temText5}>
        <View>
        <View style={{flex: 1}}>
        <Image
         style={{
           alignItems:"center",
           alignContent:"center",
           marginLeft:"5%",

         }}
