import React,{useContext,useState,useEffect} from 'react'
import { View, Text,  StyleSheet, ScrollView,TouchableOpacity, ImageBackground,Image,Dimensions,ActivityIndicator} from 'react-native';
import Search from './Search';
import Menu1 from './Menu1'
import Dashboard from './Dashboard'
import Base from './Base';
import Header from './Header';
import Footer from './Footer';
import ButtomVoir from './ButtomVoir'
import Donut from './Donut';
import {Context as AuthContext} from '../context/AuthContext';
import DateA from './DateA' ;
import {LinearGradient} from 'expo-linear-gradient';
import {
  widthPercentageToDP as wp,
  heightPercentageToDP as hp,
  listenOrientationChange as loc,
  removeOrientationListener as rol
} from 'react-native-responsive-screen';
import { ScaledSheet } from 'react-native-size-matters';
import { s, vs, ms, mvs } from 'react-native-size-matters';

console.disableYellowBox = true;

const Humidite=(props)=> {
  const [date, setDate] = React.useState({
    picture:require('../images/j1.png'),
  });  
  const {state,signin} =useContext(AuthContext);
  const [currentDate, setCurrentDate] = useState('');
  const [currentMonth, setCurrentMonth] = useState('');
  useEffect(() => {
    console.log(props.navigation.state.params.indexx)
      setCurrentDate(
        new Date().getDate()
      );
      setCurrentMonth(
        new Date().getMonth()+1
      );
    alldevice(props.navigation.state.params.indexx)  
    console.log(props.navigation.state.params.indexx)  
  }, []);
  const alldevice=(a)=>{
    if (a!=1000){ 
      setStationn({
        nom:state.reponse['devices'][a]['station'],
      })  
      setData({
        dateJ: state.reponse['data infomation']['j'].Date,
        Hrmoy:state.reponse['devices'][a]['data'][0]['j'].Hrmoy,
        Hrmin:state.reponse['devices'][a]['data'][0]['j'].Hrmin,
        Hrmax:state.reponse['devices'][a]['data'][0]['j'].Hrmax,
      })
    }
  }
  const[ stationn,setStationn]=useState({nom:state.reponse['device infomation'].station,});
  const [data, setData] = React.useState({
    dateJ: state.reponse['data infomation']['j'].Date,
    Hrmoy: state.reponse['data infomation']['j'].Hrmoy,
    Hrmin:  state.reponse['data infomation']['j'].Hrmin,
    Hrmax:  state.reponse['data infomation']['j'].Hrmax,
  }); 
  const index = props.navigation.state.params.indexx;

  let newDay5;
  let newMonth5;
  let newDate5;
  
  if((currentDate - 1) <= 0){
      if( ((currentMonth - 1) == 2) || ((currentMonth - 1) == 4) || ((currentMonth -1) == 6) || ((currentMonth -1) == 9) || ((currentMonth -1) == 11)){
          newMonth5 = (currentMonth -1);
          if((currentMonth - 1) == 2){
            if((currentDate - 1) == 0)
            newDay5 = 28;
            else
            newDay5 = 28 + (currentDate - 1)
          } else 
            if((currentDate - 1) == 0)
              newDay5 = 30;
            else
              newDay5 = 30 + (currentDate - 1)
      } else
          if((currentDate - 1) == 0)
            newDay5 = 31;
          else
            newDay5 = 31 + (currentDate - 1) 
          if(currentMonth == 1)
            newMonth5 = 12;
          else
            newMonth5 = (currentMonth - 1);
  } else {
      newDay5 = (currentDate - 1);
      newMonth5 = currentMonth;
  } 
  
  if(newDay5 < 10 ){
    if(newMonth5 < 10 )
      newDate5 = '0' + (newDay5) + '\n' + '0' + newMonth5;
    else
      newDate5 = '0' + (newDay5) + '\n' + newMonth5;
  } else {
    if(newMonth5 < 10 )
      newDate5 = (newDay5) + '\n' + '0' + newMonth5;
    else
      newDate5 = (newDay5) + '\n' + newMonth5;
  }
  
  let newDate6;
  
  if(currentDate < 10 ){
    if(currentMonth < 10 )
      newDate6 = '0' + (currentDate) + '\n' + '0' + currentMonth;
    else
      newDate6 = '0' + (currentDate) + '\n' + currentMonth;
  } else {
    if(currentMonth < 10 )
      newDate6 = (currentDate) + '\n' + '0' + currentMonth;
    else
      newDate6 = (currentDate) + '\n' + currentMonth;
  }
  
  let newDay7;
  let newMonth7;
  let newDate7;
  
  if((((currentDate + 1) > 31) && ((currentMonth == 1) || (currentMonth == 3) || (currentMonth == 5) || (currentMonth == 7) || (currentMonth ==8) || (currentMonth == 10) || (currentMonth == 12))) ){
    newDay7 = (currentDate + 1) - 31; 
    if(currentMonth == 12)
      newMonth7 = 1;
    else 
      newMonth7 = currentMonth + 1;
  } else if ((((currentDate + 1) > 30) && ((currentMonth == 2) ||  (currentMonth == 4) ||  (currentMonth == 6) ||  (currentMonth == 9) ||  (currentMonth == 11)))){
      newDay7 = (currentDate + 1) - 30;
      newMonth7 = currentMonth + 1;
  } else if((((currentDate + 1) > 28) && (currentMonth == 2))){
      newDay7 = (currentDate + 1) - 28;
      newMonth7 = currentMonth + 1;
  } else {
    newDay7 = currentDate + 1;
    newMonth7 = currentMonth;
  }
  
  if(newDay7 < 10 ){
    if(newMonth7 < 10 )
      newDate7 = '0' + (newDay7) + '\n' + '0' + newMonth7;
    else
      newDate7 = '0' + (newDay7) + '\n' + newMonth7;
  } else {
    if(newMonth7 < 10 )
      newDate7 = (newDay7) + '\n' + '0' + newMonth7;
    else
      newDate7 = (newDay7) + '\n' + newMonth7;
  }
  
  let newDay8;
  let newMonth8;
  let newDate8;
  
  if((((currentDate + 2) > 31) && ((currentMonth == 1) || (currentMonth == 3) || (currentMonth == 5) || (currentMonth == 7) || (currentMonth ==8) || (currentMonth == 10) || (currentMonth == 12))) ){
    newDay7 = (currentDate + 2) - 31; 
    if(currentMonth == 12)
      newMonth8 = 1;
    else 
      newMonth8 = currentMonth + 1;
  } else if ((((currentDate + 2) > 30) && ((currentMonth == 2) ||  (currentMonth == 4) ||  (currentMonth == 6) ||  (currentMonth == 9) ||  (currentMonth == 11)))){
      newDay8 = (currentDate + 2) - 30;
      newMonth8 = currentMonth + 1;
  } else if((((currentDate + 2) > 28) && (currentMonth == 2))){
      newDay8 = (currentDate + 2) - 28;
      newMonth8 = currentMonth + 1;
  } else {
    newDay8 = currentDate + 2;
    newMonth8 = currentMonth;
  }
  
  if(newDay8 < 10 ){
    if(newMonth8 < 10 )
      newDate8 = '0' + (newDay8) + '\n' + '0' + newMonth8;
    else
      newDate8 = '0' + (newDay8) + '\n' + newMonth8;
  } else {
    if(newMonth8 < 10 )
      newDate8 = (newDay8) + '\n' + '0' + newMonth8;
    else
      newDate8 = (newDay8) + '\n' + newMonth8;
  }

    return (
      <View style={styles.container}>
        <View style={styles.header}>
          <Image  source={require('../images/ArriereHu.png')} style={styles.headerImage}/>
          <Header onPress={()=>props.navigation.navigate('Dashboard')}/>
        </View>
        <ScrollView  style={styles.scrollView}>
        <Image  source={require('../images/ArriereHu.png')} style={styles.imageBackground}/>
            <View style={styles.stationEtPosition}>
              <Text style={styles.stationText}>STATION{"\n"}{stationn.nom}</Text>  
              <Image source={require('../images/Position1.png')} style={styles.positionImage} />
            </View>
            <View style={styles.dateEtHumidite}>
              <Text style={styles.date}>{data.dateJ}</Text> 
              <Image source={require('../images/humidite1.png')} style={styles.humiditeImage} />
              <View>
                <Text style={styles.humiditeText}>Humidité relative</Text> 
                <Text style={styles.unit}>%</Text>
              </View>
            </View>
            <View style={styles.items}>
              <View style={styles.temView2}>
                <View style={styles.daysContainer}>
                      <Image
                        style={{
                          alignItems:"center",
                          alignContent:"center",
                          marginLeft: wp("2%"),
                          width: wp("12%"),
                          height: hp("30%"),
                        }}
                        source={date.picture}
                      />
                      <View style={{position: 'absolute', marginTop: hp("1%")}}>
                        <TouchableOpacity
                          onPress={()=>{
                            if (props.navigation.state.params.indexx!=1000){ 
                              setData({
                                dateJ: state.reponse['data infomation']['j-1'].Date,
                                Hrmoy:state.reponse['devices'][props.navigation.state.params.indexx]['data'][0]['j-1'].Hrmoy,
                                Hrmin:state.reponse['devices'][props.navigation.state.params.indexx]['data'][0]['j-1'].Hrmin,
                                Hrmax:state.reponse['devices'][props.navigation.state.params.indexx]['data'][0]['j-1'].Hrmax,
                              });
                            }
                            else{
                              setData({
                                dateJ: state.reponse['data infomation']['j-1'].Date,
                                Hrmoy:state.reponse['data infomation']['j-1'].Hrmoy,
                                Hrmin:state.reponse['data infomation']['j-1'].Hrmin,
                                Hrmax:state.reponse['data infomation']['j-1'].Hrmax,
                              });
                            }
                            setDate({
                              picture:require('../images/jm1.png')
                            })
                          }}
                        >
                          <DateA date2={newDate5}/>
                        </TouchableOpacity>
                      </View>
                      <View style={{position: 'absolute', marginTop: hp("7.5%")}}>
                        <TouchableOpacity
                          onPress={()=>{
                            if (props.navigation.state.params.indexx!=1000){ 
                              setData({
                                dateJ: state.reponse['data infomation']['j'].Date,
                                Hrmoy:state.reponse['devices'][props.navigation.state.params.indexx]['data'][0]['j'].Hrmoy,
                                Hrmin:state.reponse['devices'][props.navigation.state.params.indexx]['data'][0]['j'].Hrmin,
                                Hrmax:state.reponse['devices'][props.navigation.state.params.indexx]['data'][0]['j'].Hrmax,
                              });
                            }
                            else{
                              setData({
                                dateJ: state.reponse['data infomation']['j'].Date,
                                Hrmoy:state.reponse['data infomation']['j'].Hrmoy,
                                Hrmin:state.reponse['data infomation']['j'].Hrmin,
                                Hrmax:state.reponse['data infomation']['j'].Hrmax,
                              });
                            }
                            setDate({picture:require('../images/j1.png')})
                          }}
                        >
                          <DateA date2={newDate6}/>
                        </TouchableOpacity>
                      </View>
                      <View style={{position: 'absolute',marginTop: hp("13.5%")}}>
                        <TouchableOpacity
                          onPress={()=>{
                            if (props.navigation.state.params.indexx!=1000){ 
                              setData({
                                dateJ: state.reponse['data infomation']['j+1'].Date,
                                Hrmoy:state.reponse['devices'][props.navigation.state.params.indexx]['data'][0]['j+1'].Hrmoy,
                                Hrmin:state.reponse['devices'][props.navigation.state.params.indexx]['data'][0]['j+1'].Hrmin,
                                Hrmax:state.reponse['devices'][props.navigation.state.params.indexx]['data'][0]['j+1'].Hrmax,
                              });
                            }
                            else{
                              setData({
                                dateJ: state.reponse['data infomation']['j+1'].Date,
                                Hrmoy:state.reponse['data infomation']['j+1'].Hrmoy,
                                Hrmin:state.reponse['data infomation']['j+1'].Hrmin,
                                Hrmax:state.reponse['data infomation']['j+1'].Hrmax,
                              });
                            }
                            setDate({picture:require('../images/jm2.png')})
                          }}
                        >
                          <DateA date2={newDate7}/>
                        </TouchableOpacity  >
                      </View>
                      <View style={{position: 'absolute',marginTop: hp("20%")}}>
                        <TouchableOpacity  
                          onPress={()=>{
                            if (props.navigation.state.params.indexx!=1000){ 
                              setData({
                                dateJ: state.reponse['data infomation']['j+2'].Date,
                                Hrmoy:state.reponse['devices'][props.navigation.state.params.indexx]['data'][0]['j+2'].Hrmoy,
                                Hrmin:state.reponse['devices'][props.navigation.state.params.indexx]['data'][0]['j+2'].Hrmin,
                                Hrmax:state.reponse['devices'][props.navigation.state.params.indexx]['data'][0]['j+2'].Hrmax,
                            });
                            }
                            else{
                              setData({
                                dateJ: state.reponse['data infomation']['j+2'].Date,
                                Hrmoy:state.reponse['data infomation']['j+2'].Hrmoy,
                                Hrmin:state.reponse['data infomation']['j+2'].Hrmin,
                                Hrmax:state.reponse['data infomation']['j+2'].Hrmax,
                              });
                            }
                            setDate({picture:require('../images/jm3.png')})
                          }}
                        >
                          <DateA date2={newDate8}/>
                        </TouchableOpacity>
                      </View>
                </View>
                <View style={styles.graph}>
                  <View>
                    <Donut color='#3D57A7' percentage={parseInt(data.Hrmax)}/> 
                  </View>
                  <View>
                    <Donut color='#00BBEA' percentage={parseInt(data.Hrmoy)} />
                  </View>
                  <View>
                    <Donut color='#75CEDE' percentage={parseInt(data.Hrmin)}/>
                  </View>
                </View>
              </View>
              <View style={styles.voirPlusContainer}>
                <TouchableOpacity onPress={()=>props.navigation.navigate('humiditegraph',{index})}>
                  <LinearGradient
                    colors={['#0B193C', '#0B193C']}
                    style={styles.signIn2}
                  >
                    <Text style={styles.voirPlusText}>Voir Plus</Text>
                  </LinearGradient>
                </TouchableOpacity>
              </View>
          </View>
              <Text></Text>
              <Text></Text>
              <Text></Text>
        </ScrollView > 
        <Footer/>
      </View>
    );
}

export default Humidite;

const styles = ScaledSheet.create({
  container:{
    flex: 1,
  },

  header:{

  },

  headerImage: {
    height: hp('12%'),
    width: wp('100%'),
    position: 'absolute',
    //Swidth:240,
    alignContent: "center", 
    //flex:1,
  },

  scrollView: {
    backgroundColor:"#F8FAFC" 
  },

  imageBackground: {
    height: hp("59%"),
    width: wp("100%"),
    paddingTop: hp("2%"),
    position: 'absolute',
    marginTop: hp("-25%")
  },

  stationEtPosition: {
    flexDirection: 'row',
  },

  positionImage:{
    marginLeft: wp("13%"),
    marginTop: hp("3%")
  },

  stationText: {
    fontSize: '30@s',
    color: '#FFFFFF',
    marginTop: hp("2%"),
    margin: wp("2%"),
    marginLeft: wp("6%"),
    fontFamily: 'OpenSans-Bold', 
    fontWeight: 'bold'
  },
  
  dateEtHumidite: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    //alignItems:'center',
    backgroundColor: 'white',
    height:  hp("16%"),
    width: wp("90%"),
    borderRadius: 9.29,
    marginTop: hp("3%"),
    marginBottom: hp("5%"),
    marginRight: wp("5%"),
    marginLeft: wp("5%"),
    shadowColor: "#000",
    shadowOffset: {
      width: '0@s',
      height: '16@vs',
    },
    shadowOpacity: 0.58,
    shadowRadius: 16.00,
    elevation: 24,
    },

  date:{
    marginTop: hp("5.5%"),
    marginLeft: wp("2%"),
    fontFamily: "Poppins-Regular",
    fontSize: '13.93@s',
    fontWeight: 'bold'
  },

  humiditeText:{
    marginTop: hp("5.5%"),
    marginRight: wp("2%"),
    fontWeight: 'bold',
    fontFamily: "Poppins-Regular",
    fontSize: '13.93@s',
  },

  unit:{
    fontWeight: 'bold',
    fontFamily: "Poppins-Regular",
    fontSize: '20.13@s',
  },

  humiditeImage:{
    marginTop: hp("2%"), 
  },

  temView2: {
    flexDirection: 'row',
    marginTop: hp("0%")
  },

  daysContainer: {   
    marginLeft: wp("3%"),
    marginRight: wp("5%"),
    marginTop: hp("9%")
  },

  graph:{
    marginLeft: wp("18%"), 
    marginTop: hp('1%')
  },

  voirPlusContainer: {   
    marginLeft: wp("30%"),
    marginTop: hp("1%")
  },

  signIn2: {
    width: wp("40%"),
    height: hp("7%"),
    justifyContent: 'center',
    alignItems: 'center',
    borderRadius: 14, //50
    flexDirection: 'row',
  },

  voirPlusText: {
    color: 'white',
    fontWeight: 'bold',
    fontSize: '19@s'
  },
})
