import { StatusBar } from 'expo-status-bar';
import React from 'react';
import { Animated,ImageBackground,StyleSheet, Text, View ,Image} from 'react-native';
//import Animated, { Easing } from 'react-native-reanimated';
import {
  widthPercentageToDP as wp,
  heightPercentageToDP as hp,
  listenOrientationChange as loc,
  removeOrientationListener as rol
} from 'react-native-responsive-screen';
import { ScaledSheet } from 'react-native-size-matters';
import { s, vs, ms, mvs } from 'react-native-size-matters';

const Progress=({step, steps, height,colorb}) =>{
    const animatedValue=React.useRef(new Animated.Value(-1000)).current;
    const reactive=React.useRef(new Animated.Value(-1000)).current;
    const [width, setWidth]=React.useState(0);
    React.useEffect(() =>{
        Animated.timing(animatedValue, {
          toValue:reactive,
          duration:400,
          
          //easing:Easing.bounce(),
          useNativeDriver: true,

        }).start();
    }, []);
    React.useEffect(() => {
        reactive.setValue(-width +(width*step)/steps);
    }, [step, width]);
    

    return ( 
   
    <View   
      onLayout={(e) =>{
        const newWidth =e.nativeEvent.layout.width;
        setWidth(newWidth);
      }}
      style={styles.view1}
    >    
      <ImageBackground
        source={require('../images/tub.png')}
        style={{position:'absolute'}}
      />
      <Image   source={require('../images/bare.png')}
        style={styles.image}
      />
      <Animated.View  
        style={{
          height:hp('9%'),
          width: wp('64%'),
          backgroundColor: '#C2B49A',
          //borderRadius: height,
          zIndex:1,
          position:'absolute',
          //borderTopRightRadius:0,
          //borderBottomRightRadius:0,
          //position:'absolute',
          //marginLeft :100,
          //top:27,
          transform:[{translateX:animatedValue,},],
        }}
      />  
    </View>      

)}
const ProgressBarr=(props) => {

  return (
    <View style={styles.container}>
      <View style={styles.transform} >
        <View>
          <Image   source={require('../images/rectangle.png')} style={styles.transformm} />
        </View>   
        <Progress 
          step={props.dataa}  steps={15} 
          height={0} 
        />
      </View>
    </View>
  );
}


;
export default ProgressBarr;

const styles = ScaledSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    marginTop: hp("-1%"),
  },

  transformm:{
    transform: [{ rotate: "0deg" }, ],
    marginTop: hp("-3.5%"),
    marginLeft: wp("63%"),
    width: wp("14%"),
    height: hp("16%")
  },
 
  transform:{
    transform: [{ rotate: "-90deg" },],
    marginTop: hp("7%")
  },

  view1:{
    position:'absolute',
    width: wp("64%"),
    marginLeft: wp("-1%"), //-65
    height: hp("9%"),
    backgroundColor:'#F2EAE4',
    // borderRadius: height,
    overflow:"hidden",
  },

  image:{
    height: hp("2%"),
    marginTop: hp("0.5%"),
    zIndex:2,
    position:'absolute',
  },

});
