import React,{Component} from 'react'
import { View, 
    Text, 
    TouchableOpacity, 
    Dimensions,
    StyleSheet,
    Image,
    ImageBackground,
} from 'react-native';
import {LinearGradient} from 'expo-linear-gradient';
import MaterialIcons from 'react-native-vector-icons/MaterialIcons';
import {
    widthPercentageToDP as wp,
    heightPercentageToDP as hp,
    listenOrientationChange as loc,
    removeOrientationListener as rol
} from 'react-native-responsive-screen';
import { ScaledSheet } from 'react-native-size-matters';
import { s, vs, ms, mvs } from 'react-native-size-matters';
import Langue2 from './Langue2';

console.disableYellowBox = true;

const LoadingScene=(props) =>{
    return ( 
            <View  style={styles.container}>
                <View style={styles.view1}>
                    <Image
                        style={styles.greenImage}
                        source={require('../images/green.png')}
                        resizeMode="stretch"
                    />
                    <Langue2 style={styles.langue}/>  
                </View>
                <View style={styles.view2}>
                    <Image
                        source={require('../images/loogo.png')}
                    />
                    <Text style={styles.title}>SMART IRRIGATION</Text>
                    <Text style={styles.title}> APP</Text>
                    <TouchableOpacity onPress={()=> props.navigation.navigate('Login')}>
                        <LinearGradient
                            colors={['#56CCF2', '#2F80ED']}
                            style={styles.signIn}
                        >
                            <Text style={styles.textSign}>S'identifier</Text>
                        </LinearGradient>
                    </TouchableOpacity>
                </View>
                <View style={styles.view3}>
                    <Image  
                        source={require('../images/blue.png')}
                        style={styles.blueImage}  
                    />
                    <Image  style={styles.yobeen} source={require('../images/yobeen.png')}/>
                </View>
            </View>
    );
};

export default LoadingScene;

const {height} = Dimensions.get("screen");
const height_logo = height * 0.28;
const styles = ScaledSheet.create({
    container:{
        flex: 1,
    },

    view1:{
        alignItems: 'flex-end' ,
        justifyContent: 'flex-start' ,
    },

    langue:{
        marginRight: wp("12%")
    },

    greenImage:{
        width:wp('100%'),
        height: hp("30%"),
        marginRight: wp("-1%"),
        marginTop: hp("-4%")
    },

    view2: {
        justifyContent: 'center',
        alignItems: 'center',
    },

    title: {
        color: '#05375a',
        fontSize:'30@s',
        fontWeight: 'bold',
        fontFamily:'Roboto-Black',
    },

    signIn: {
        width: wp("45%"),
        height: hp('5%'),
        justifyContent: 'center',
        alignItems: 'center',
        borderRadius: 14, 
        marginTop: hp("10%"),
    },
 
    textSign: {
        color: 'white',
        fontWeight: 'bold',
        fontSize:'14@s'
    },
    
    view3: {
        marginTop: hp("15%")
    },
    
    blueImage: {
        position: 'absolute',
        marginTop: hp("-10%"),
    },  

    yobeen:{
        marginTop: hp("5%") ,
        marginLeft: wp("70%")
    },

});
