import React, { useState, useEffect, useContext } from 'react'
import { View, Modal, Text, Pressable, Linking, StyleSheet, TouchableOpacity, ScrollView, ImageBackground, Image, Dimensions, ActivityIndicator } from 'react-native';
import Search from './Search';
import Header from './Header';
import Footer from './Footer';

import Menu1 from './Menu1'
import Dashboard from './Dashboard'
import Base from './Base'
import ButtomVoir from './ButtomVoir'
import ProgressBar from "./ProgressBar"
import { Context as AuthContext } from '../context/AuthContext';
import DateA from './DateA';
import { LinearGradient } from 'expo-linear-gradient';
const Temperature = (props) => {
  const { state, signin } = useContext(AuthContext);
  const [currentDate, setCurrentDate] = useState('');
  const [currentMonth, setCurrentMonth] = useState('');
  useEffect(() => {
    console.log(props.navigation.state.params.indexx)
    setCurrentDate(
      new Date().getDate()
    );
    setCurrentMonth(
      new Date().getMonth() + 1
