import React, {Component,useState,useContext,useEffect} from 'react';
import {View,Dimensions,Text,ScrollView,Image,StyleSheet,TouchableOpacity} from 'react-native'
import {LineChart } from 'react-native-chart-kit'
import { Rect, Text as TextSVG, Svg } from "react-native-svg";
import {Context as AuthContext} from '../context/AuthContext';
import {LinearGradient} from 'expo-linear-gradient';
import DatePicker from 'react-native-datepicker';
import Header from './Header';
import Footer from './Footer';
import { VictoryAxis,VictoryGroup,VictoryLabel, VictoryChart, VictoryTheme,VictoryLine,VictoryArea,VictoryClipContainer,VictoryVoronoiContainer,VictoryTooltip } from "victory-native";
const screenWidth = Dimensions.get("window").width;
function float2int (value) {
  return value | 0;
}
const  humiditegraph=(props) =>  {
  let [tooltipPos, setTooltipPos] = useState({ x: 0, y: 0, visible: false, value: 0 })
  const {state,signin} =useContext(AuthContext);
  const [currentDate, setCurrentDate] = useState('');
  const [currentMonth, setCurrentMonth] = useState('');
  useEffect(() => {
    console.log(props.navigation.state.params.index)
      setCurrentDate(
        new Date().getDate()
      );
      setCurrentMonth(
        new Date().getMonth()+1
      );
      alldevice(props.navigation.state.params.index)    
  }, []);
  const alldevice=(a)=>{
    if (a!=1000){
      setStationn({
        pre:state.reponse['devices'][a]['data'][0],
        nom:state.reponse['devices'][a]['station'],
      });
    }
  }
  const indexx=props.navigation.state.params.index;
  const[ stationn,setStationn]=useState({
    pre:state.reponse['data infomation'],
    nom:state.reponse['device infomation'].station,
  });
  const datamin = [
    { x:(currentDate-5)+'/'+'0'+currentMonth, y:float2int(stationn.pre['j-5'].Hrmin),z:stationn.pre['j-5'].Hrmin},
    { x:(currentDate-4)+'/'+'0'+currentMonth, y:float2int(stationn.pre['j-4'].Hrmin),z:stationn.pre['j-4'].Hrmin},
    { x:(currentDate-3)+'/'+'0'+currentMonth, y:float2int(stationn.pre['j-3'].Hrmin),z:stationn.pre['j-3'].Hrmin},
    { x:(currentDate-2)+'/'+'0'+currentMonth, y:float2int(stationn.pre['j-2'].Hrmin),z:stationn.pre['j-2'].Hrmin},
    { x:(currentDate-1)+'/'+'0'+currentMonth, y:float2int(stationn.pre['j-1'].Hrmin),z:stationn.pre['j-1'].Hrmin},
    { x:(currentDate)+'/'+'0'+currentMonth, y:float2int(stationn.pre['j'].Hrmin),z:stationn.pre['j'].Hrmin},
    { x:(currentDate+1)+'/'+'0'+currentMonth, y:float2int(stationn.pre['j+1'].Hrmin),z:stationn.pre['j+1'].Hrmin},
    { x:(currentDate+2)+'/'+'0'+currentMonth, y:float2int(stationn.pre['j+2'].Hrmin),z:stationn.pre['j+2'].Hrmin},
  ];

  const datamoy = [
    { x:(currentDate-5)+'/'+'0'+currentMonth, y:float2int(stationn.pre['j-5'].Hrmoy),z:stationn.pre['j-5'].Hrmoy},
    { x:(currentDate-4)+'/'+'0'+currentMonth, y:float2int(stationn.pre['j-4'].Hrmoy),z:stationn.pre['j-4'].Hrmoy},
    { x:(currentDate-3)+'/'+'0'+currentMonth, y:float2int(stationn.pre['j-3'].Hrmoy),z:stationn.pre['j-3'].Hrmoy},
