import React,{useContext, useState, useEffect } from 'react'
import { View, Text,  StyleSheet, ScrollView, Image} from 'react-native';
import Header from './Header';
import Footer from './Footer';
import {Context as AuthContext} from '../context/AuthContext';
import { TouchableOpacity } from 'react-native-gesture-handler';
import { VictoryBar,VictoryZoomContainer ,VictoryAxis, VictoryChart,VictoryLabel,VictoryTheme } from "victory-native";
const RayonnementGraphe=(props) =>{
const {state,signin} =useContext(AuthContext);
const [currentDate, setCurrentDate] = useState('');
const [currentMonth, setCurrentMonth] = useState('');
    useEffect(() => {
        setCurrentDate(
            new Date().getDate()
        );
        setCurrentMonth(
            new Date().getMonth()+1
        );
      
         
          
      }, []);

      function float2int (value) {
        return value | 0;
      }

  const data3 = [
    { quarter:(currentDate-5)+'/'+'0'+currentMonth, earnings:float2int(state.reponse['data infomation']['j-5'].Rg),label:state.reponse['data infomation']['j-5'].Rg},
    { quarter:(currentDate-4)+'/'+'0'+currentMonth, earnings:float2int(state.reponse['data infomation']['j-4'].Rg),label:state.reponse['data infomation']['j-4'].Rg},
    { quarter:(currentDate-3)+'/'+'0'+currentMonth, earnings:float2int(state.reponse['data infomation']['j-3'].Rg),label:state.reponse['data infomation']['j-3'].Rg},
    { quarter:(currentDate-2)+'/'+'0'+currentMonth, earnings:float2int(state.reponse['data infomation']['j-2'].Rg),label:state.reponse['data infomation']['j-2'].Rg},
    { quarter:(currentDate-1)+'/'+'0'+currentMonth, earnings:float2int(state.reponse['data infomation']['j-1'].Rg),label:state.reponse['data infomation']['j-1'].Rg},
    { quarter:(currentDate)+'/'+'0'+currentMonth,   earnings:float2int(state.reponse['data infomation']['j'].Rg),label:state.reponse['data infomation']['j'].Rg},
    { quarter:(currentDate+1)+'/'+'0'+currentMonth, earnings:float2int(state.reponse['data infomation']['j+1'].Rg),label:state.reponse['data infomation']['j+1'].Rg},
    { quarter:(currentDate+2)+'/'+'0'+currentMonth, earnings:float2int(state.reponse['data infomation']['j+2'].Rg),label:state.reponse['data infomation']['j+2'].Rg},
  ];
    return (
      <View style={{flex:1}}>
<ScrollView  style={styles.container}>         
<Image  source={require('../images/ArruereR.png')} style={styles.action}>
         </Image>
         <Header
      onPress={()=>props.navigation.navigate('menu')}
     />
   
           <View style={styles.temView3}>
           <Text style={styles.temText1}>STATION{"\n"}{state.reponse['device infomation'].station}</Text>  
           <Image source={require('../images/Position1.png')} style={styles.Image} />
           </View>
         
    <View style={styles.Cont}>
                    <Text style={styles.Cont1}>{state.reponse['data infomation']['j'].Date}</Text> 
                    <Image source={require('../images/soleil.png')} style={styles.ImageB} />
<View>
                    <Text style={styles.Cont2}>Rayonnement
                    
                   
                    </Text> 
                     <Text style={styles.Cont3}>J/cm²</Text>
                     </View>
                    </View>
                    <View style={styles.gra}>
        <VictoryChart 

        width={410} 
        height={400}
        theme={VictoryTheme.material}
        domainPadding={10}
        
      
        domain={{}}>
          <VictoryBar horizontal
          barWidth={13}
          data={data3} x="quarter" y= "earnings"
          style={{ data: { fill: "#F0CB35" } }}
          //alignment="start"
          animate={{
            duration: 2000,
            onLoad: { duration: 1000 }
          }}
          alignment="middle"
          labelComponent={
            <VictoryLabel verticalAnchor="middle"
            dy={-20}
            dx={28} textAnchor="end"/>
          }
          />
          <VictoryAxis
 style={{
   tickLabels: {fontSize: 14, padding: 5,color:"transparent",fontWeight:'bold'}
 }}
/>
        </VictoryChart>
        
      
      </View>

   </ScrollView>
   <Footer/>
   </View>
    
    );
};
export default RayonnementGraphe;
const styles = StyleSheet.create({
    action: {
      height:"55%",
      width:"100%",
      paddingTop:40,
      position:'absolute',
      marginTop:-143
    },
    text: {
        
    },
    temView2: {
        flexDirection: 'row',
        marginTop:90
      },
      temT: {
        marginTop:30,
alignSelf:"center",
        flexDirection: 'row',
        
      },
      temView3: {
        flexDirection: 'row',
        
      },
      temText: {
        
        fontSize:16,
        textAlign:"center",
        color:'#FFFFFF',
        marginBottom:10,

      },
      temText4: {
       
        fontSize:17,
        color:'#FFFFFF',
        margin:5,
        marginLeft:20

      },
      Cont: {
        //alignItems:'center',
        
        backgroundColor: 'white',
        height:110.42,

        width:"90%",
        margin:"5%",
        borderRadius: 9.29,
        marginRight:"5%",
        marginLeft:"5%",
        shadowColor: "#000",
        shadowOffset: {
          width: 0,
          height: 16,
        },
        shadowOpacity: 0.58,
        shadowRadius: 16.00,
        
        elevation: 24,
        flexDirection: 'row',
      },
    
      Cont1:{
       
        marginTop:"12%",
  marginLeft:"6%",
  marginRight:"-2%",
        fontFamily:"Poppins-Regular",
        fontSize:13.93,
        fontWeight:'bold',

        
      
      },
      Cont2:{
      
        marginTop:"26%",
        marginLeft:"5%",
        fontWeight:'bold',
marginRight:12,
        fontFamily:"Poppins-Regular",
        fontSize:13.93,
      },
      Cont3:{
        marginLeft:"30.5%",
        fontWeight:'bold',
        fontFamily:"Poppins-Regular",
        fontSize:18.13 ,
      },
      ImageB:{
        marginLeft:"14%",
        marginTop:"4%",
        marginRight:"4%"
      },
      temText1: {
       
        fontSize:30,
        color:'#FFFFFF',
        margin:10,
        marginLeft:20,
fontWeight:'bold',
        marginBottom:0

      },
      temText5: {   
        marginLeft:10,
        marginRight:10,
        
      },
      temText6: {   
        marginLeft:5,
        marginTop:50
       
       },
       
       temText7: {   
        marginLeft:100,
        marginTop:100
       
       },
    container: {
        flex:1,  
        backgroundColor:'#fff'
       
    },
    Image:{
      marginLeft:60,
      marginTop:30
     
    },
    temText8: {
       
      fontSize:20,
      color:'#F15C22',
      
     textAlign:'center'

    },
    image1:{
      marginLeft:9,
      marginRight:25
    },
    image2:{
      marginLeft:35,
     
    },
    gra: {
      //flex: 1,
      justifyContent: "center",
      alignItems: "center",
      marginLeft:"7%",
      marginTop:"13%"
      //backgroundColor: "#f5fcff"
    }
  

}
);
