import React,{Component} from 'react';
import { 
    View, 
    Text, 
    TouchableOpacity, 
    Dimensions,
    StyleSheet,
    StatusBar,
    Image,
    Button
} from 'react-native';

import LinearGradient from 'react-native-linear-gradient';


class Authentification extends Component{
    
render(){
    return (
      <View style={styles.container}>
        <View style={styles.header}>
        <Image
            style={{width:250,height:250,margin:325}}
             source={require('../images/logggo.png')}
             style={styles.logo}
            resizeMode="stretch"
            />
           
        </View>
        <View style={styles.footer}>
        <Text style={[styles.title, {
                color: colors.text
            }]}>Cultivating ideas for growth</Text>
            <Text style={styles.text}>Connectez vous</Text>
            <View style={styles.button}>
                <LinearGradient
                    colors={['#08d4c4', '#01ab9d']}
                    style={styles.signIn}
                >
                    <Text style={styles.textSign}>Get Started</Text>
               
                </LinearGradient>
            </View>
        </View>
        
      </View>
    );
}


};

export default Authentification;

const {height} = Dimensions.get("screen");
const height_logo = height * 0.28;


const styles = StyleSheet.create({
  container: {
    flex: 1, 
    backgroundColor: '#009387'
  },
  header: {
      flex: 2,
      justifyContent: 'center',
      alignItems: 'center'
  },
  footer: {
      flex: 1,
      backgroundColor: '#fff',
      borderTopLeftRadius: 30,
      borderTopRightRadius: 30,
      paddingVertical: 50,
      paddingHorizontal: 30
  },
  logo: {
    width: height_logo,
    height: height_logo
},
title: {
    color: '#05375a',
    fontSize: 30,
    fontWeight: 'bold'
},
text: {
    color: 'grey',
    marginTop:5
},
signIn: {
    width: 150,
    height: 40,
    justifyContent: 'center',
    alignItems: 'center',
    borderRadius: 50,
    flexDirection: 'row'
},
textSign: {
    color: 'white',
    fontWeight: 'bold'
},
button: {
    alignItems: 'flex-end',
    marginTop: 30
},
}
);
