import React,{Component} from 'react';
import { 
    View, 
    Text, 
    TouchableOpacity, 
    TextInput,
    Platform,
    StyleSheet ,
    StatusBar,
    Alert,
    Image
} from 'react-native';
import * as Animatable from 'react-native-animatable';
import {LinearGradient} from 'expo-linear-gradient';
import Feather from 'react-native-vector-icons/Feather';
import Langue2 from './Langue2';

console.disableYellowBox = true;

const MdpRein=(props) => {

    const [data, setData] = React.useState({
        username: '',
        password: '',
        check_textInputChange: false,
        secureTextEntry: true,
        isValidUser: true,
        isValidPassword: true,
    });

    const handlePasswordChange = (val) => {
        if( val.trim().length >= 8 ) {
            setData({
                ...data,
                password: val,
                isValidPassword: true
            });
        } else {
            setData({
                ...data,
                password: val,
                isValidPassword: false
            });
        }
    }

    const updateSecureTextEntry = () => {
        setData({
            ...data,
            secureTextEntry: !data.secureTextEntry
        });
    }

    const textInputChange = (val) => {
        if( val.trim().length == 10  ) {
            setData({
                ...data,
                username: val,
                check_textInputChange: true,
            });
        } else {
            setData({
                ...data,
                username: val,
                check_textInputChange: false,
            });
        }
    }

    return (
        <View style={styles.container}>
            <Image
                style={styles.header}
                source={require('../images/green.png')}
                resizeMode="stretch"
            />
            <Langue2/>
            <Text style={styles.msg}>Réinitialiser le mot de passe </Text>
            <LinearGradient
                colors={['#fff', '#fff']}
                style={[styles.signIn, {
                    borderColor: '#7FD0D7',
                    borderWidth: 2,
                    marginTop: 15,
                    borderRadius: 50,
                    marginBottom:15  
                }]}
            >
                <View style={styles.action}>
                    <Image
                        style={{marginLeft:15}}
                       source={require('../images/lock.png')}
                      />
                 <TextInput 
                    placeholder="Nouveau Mot De Passe"
                    placeholderTextColor="#7FD0D7"
                    secureTextEntry={data.secureTextEntry ? true : false}
                    underlineColorAndroid="transparent"
                    style={styles.textInput}
                    autoCapitalize="none"
                />
                    <TouchableOpacity
                        style={{marginRight:20}}
                        onPress={updateSecureTextEntry}
                    >
                        {data.secureTextEntry ? 
                            <Image
                                style={{marginLeft:15}}
                                source={require('../images/eye.png')}
                            />
                        :
                            <Image
                                style={{marginLeft:15}}
                                source={require('../images/eye.png')}
                            />
                        }
                    </TouchableOpacity>
                </View>
            </LinearGradient>
            <LinearGradient
                colors={['#fff', '#fff']}
                style={[styles.signIn, {
                    borderColor: '#7FD0D7',
                    borderWidth: 2,
                    marginTop: 15,
                    borderRadius: 50,
                    marginBottom:15  
                }]}
            >
                <View style={styles.action}>
                    <Image
                        style={{marginLeft:15}}
                       source={require('../images/lock.png')}
                    />
                    <TextInput 
                        placeholder="Confirmer votre mot de passe"
                        placeholderTextColor="#7FD0D7"
                        secureTextEntry={data.secureTextEntry ? true : false}
                        nderlineColorAndroid="transparent"
                        style={styles.textInput}
                        autoCapitalize="none"
                    />
                    <TouchableOpacity
                        style={{marginRight:20}}
                        onPress={updateSecureTextEntry}
                    >
                        {data.secureTextEntry ? 
                            <Image style={{marginLeft:15}} source={require('../images/eye.png')}/>
                        :
                            <Image style={{marginLeft:15}} source={require('../images/eye.png')}/>
                        }
                    </TouchableOpacity>
                </View>
            </LinearGradient>
            <TouchableOpacity
                style={[styles.signIn,{
                    width:'100%',
                }]}
            >
                <LinearGradient
                    colors={['#118DCD', '#118DCD']}
                    style={styles.signIn}
                >
                    <Text style={[styles.textSign, {color:'#fff'}]}>Enregistrer </Text>
                </LinearGradient>
            </TouchableOpacity>
            <Image
                style={styles.ciel}
                source={require('../images/ciel.png')}
                resizeMode="stretch"
            />
        </View>
    );
};

export default MdpRein;

const styles = StyleSheet.create({
    container: {
        flex: 1, 
        backgroundColor: '#fff',
        alignItems:'center',
        // marginTop:30,
    },

    header: {
        marginLeft:20,
    },

    footer: {
        flex: 1,
        backgroundColor: '#fff',
        borderTopLeftRadius: 30,
        borderTopRightRadius: 30,
        paddingHorizontal: 20,
        paddingVertical: 30,
        alignItems:'center',
        
    },

    ciel:{
        marginRight:20,
        marginTop:"16.4%"
    },

    text_header: {
        color: '#05375a',
        fontWeight: 'bold',
        fontSize: 30,
    },

    text_footer: {
        color: '#05375a',
        fontSize: 18,
    },

    action: {
        flexDirection: 'row',
        marginTop: 10,
        borderBottomWidth: 1,
        borderBottomColor: '#f2f2f2',
        paddingBottom: 5,
    },
    
    actionError: {
        flexDirection: 'row',
        marginTop: 10,
        borderBottomWidth: 1,
        borderBottomColor: '#FF0000',
        paddingBottom: 5
    },

    textInput: {
        flex: 1,
        marginTop: Platform.OS === 'ios' ? 0 : -12,
        paddingLeft: 10,
        color: '#05375a',
    },

    errorMsg: {
        color: '#FF0000',
        fontSize: 14,
    },

    button: {
        alignItems: 'center',
        marginTop: 10
    },

    signIn: {
        width: '80%',
        height: 50,
        justifyContent: 'center',
        alignItems: 'center',
        borderRadius: 10,
    },

    textSign: {
        fontSize: 18,
        fontWeight: 'bold'
    },
    
    msg:{
         textAlign:'center',
         fontWeight: 'bold',
         fontSize: 17,
         marginTop:"12%"  
    }
});
