import React,{Component} from 'react';
import { 
    View, 
    Text,
    StyleSheet ,
    Image,
    Linking   
} from 'react-native';
import { Colors }from 'react-native/Libraries/NewAppScreen';
import FontAwesome from 'react-native-vector-icons/FontAwesome';
import MaterialIcons from 'react-native-vector-icons/MaterialIcons';
import {
  widthPercentageToDP as wp,
  heightPercentageToDP as hp,
  listenOrientationChange as loc,
  removeOrientationListener as rol
} from 'react-native-responsive-screen';
import { MenuProvider } from 'react-native-popup-menu';
import {
  Menu,
  MenuOptions,
  MenuOption,
  MenuTrigger,
} from 'react-native-popup-menu';
import { TouchableOpacity } from 'react-native-gesture-handler';

console.disableYellowBox = true;

const nom ="safae";
  const Langue2=(props) =>{ 
    const handleEmailPress =() => {
      Linking.openURL("mailto:agrotech@yobeen.com?subject=Email from the Agro tech mobile application  &body='Bonjour!'");
    }
    const handleWebPress =() => {
      Linking.openURL("https://google.com");
    }
    const handleCallPress =() => {
      Linking.openURL("tel:+212660111666");
    }
    const handleSmsPress =() => {
      Linking.openURL("sms:+212660111666");
    }

//meme numero pour wtsp 

  return (
    <View style={{position: 'absolute',flex: 1,alignItems: 'center',justifyContent: 'center',}}>
      <MenuProvider style={{height: hp("25%") ,width: wp("100%"),marginTop:hp("6%"),marginLeft:wp("12%")}}>
        <Menu >
          <MenuTrigger style={{width: wp("100%")}}>
            <Image  
              source={require('../images/barr.png')}
              style={{marginLeft:wp("70.6%"),}}
            />
          </MenuTrigger> 
          <MenuOptions style={styles.menu}>
            <MenuOption>
              <Text style={styles.titre}>Contactez-nous</Text>
              <View   style={{marginLeft:wp("3%")}}>
                <View style={styles.roww}>
                <TouchableOpacity   onPress={handleEmailPress} >
                  <MenuOption style={styles.row}>
                    <Image source={require('../images/email.png')} style={{position:'absolute', marginTop: hp("1%")}}/>
                    {/* <Text style={{fontWeight:'bold',color:'#118DCD',marginLeft:wp("1.5%"), marginTop: hp("1%")}}>G</Text> */}
                    <Text  style={{fontSize:wp('4%'),position:'absolute',marginLeft:wp('15%'),  marginTop: hp("1%") }} >Email</Text>
                  </MenuOption>
                </TouchableOpacity>
                </View>
                <View style={styles.roww}>
                <TouchableOpacity   onPress={handleCallPress} >
                  <MenuOption style={styles.row}>
                    <Image source={require('../images/tlphn.png')} style={{position:'absolute', marginTop: hp("1%")}}/>
                    <Text  style={{fontSize:wp('4%'),position:'absolute',marginLeft:wp('15%'), marginTop: hp("1%")}} >Appel</Text>
                  </MenuOption>
                </TouchableOpacity>
                </View>
                <View style={styles.roww}>
                <TouchableOpacity   onPress={handleSmsPress}>
                  <MenuOption style={styles.row}>
                    <Image source={require('../images/email.png')} style={{position:'absolute',  marginTop: hp("1%") }}/>
                    <Text  style={{fontSize:wp('4%'),position:'absolute',marginLeft:wp('15%'),  marginTop: hp("1%")}} >Sms</Text>
                  </MenuOption>
                </TouchableOpacity>
                </View>
              </View>
            </MenuOption>
          </MenuOptions>
        </Menu>
      </MenuProvider>
    </View>
  );
};

export default Langue2;
  const styles = StyleSheet.create({
    menu:{
      marginLeft: wp("-7%"),
      width:wp('66%'),
      height: hp("18%"),
      paddingLeft: wp("5%"),
      borderRadius:30,
      backgroundColor:'white',
      borderColor:'#283c86',
      borderWidth:1
    },
    titre:{
      color: '#118DCD',
      fontWeight:'bold',
      fontSize: wp('4.5%')
    },
    roww:{
      height: hp("3%"),
      width: wp("60%"),
      marginBottom: hp("1%")
    }
});
