import React,{Component} from 'react';
import { 
    View, 
    Text, 
    TouchableOpacity, 
    TextInput,
    Platform,
    StyleSheet ,
    StatusBar,
    Alert,
    Image,
  
} from 'react-native';
console.disableYellowBox = true;

import * as Animatable from 'react-native-animatable';
import {LinearGradient} from 'expo-linear-gradient';
import FontAwesome from 'react-native-vector-icons/FontAwesome';
import Feather from 'react-native-vector-icons/Feather';
import { ScrollView } from 'react-native-gesture-handler';
import Champ from './Champ' ;
import Langue2 from './Langue2';

const Login=(props) => {
    const [data, setData] = React.useState({
        username: '',
        password: '',
        check_textInputChange: false,
        secureTextEntry: true,
        isValidUser: true,
        isValidPassword: true,
    });
    const handlePasswordChange = (val) => {
        if( val.trim().length >= 8 ) {
            setData({
                ...data,
                password: val,
                isValidPassword: true
            });
        } else {
            setData({
                ...data,
                password: val,
                isValidPassword: false
            });
        }
    }

    const updateSecureTextEntry = () => {
        setData({
            ...data,
            secureTextEntry: !data.secureTextEntry
        });
    }

    const textInputChange = (val) => {
        if( val.trim().length == 10  ) {
            setData({
                ...data,
                username: val,
                check_textInputChange: true,
            });
        } else {
            setData({
                ...data,
                username: val,
                check_textInputChange: false,
            });
        }
    }

    return (
    <ScrollView style={styles.container}>
        <Image
            style={styles.header}
            source={require('../images/green.png')}
            resizeMode="stretch"
        />
        <View style={{alignItems:'center',marginTop:-60}}>
            <Champ img={require('../images/npm.png')} text="Nom complet"/>
            <Champ img={require('../images/tlphn.png')} text="Téléphone"/>
            <Champ img={require('../images/email.png')} text="Email"/>
            <Champ img={require('../images/organisme.png')} text="Organisme"/>
            <Champ img={require('../images/npm.png')} text="Nom complet"/>
            <Champ img={require('../images/fonction.png')} text="Fonction"/>
            <Champ img={require('../images/region.png')} text="Région"/>
            <LinearGradient
                colors={['#fff', '#fff']}
                style={[styles.signIn, {
                    borderColor: '#7FD0D7',
                    borderWidth: 2,
                    marginTop: 15,
                    borderRadius: 50,
                    marginBottom:15  
                }]}
            >
                <View style={styles.action}>
                    <Image
                        style={{marginLeft:15}}
                       source={require('../images/lock.png')}
                    />
                    <TextInput 
                        placeholder="Entrez votre mot de passe"
                        placeholderTextColor="#7FD0D7"
                        secureTextEntry={data.secureTextEntry ? true : false}
                        style={styles.textInput}
                        autoCapitalize="none"
                    />
                    <TouchableOpacity
                        style={{marginRight:20}}
                        onPress={updateSecureTextEntry}
                    >
                        {data.secureTextEntry ? 
                            <Image style={{marginLeft:15}} source={require('../images/eye.png')}/>
                        :
                            <Image source={require('../images/eye.png')}/>
                        }
                    </TouchableOpacity>
                </View>
            </LinearGradient>
        </View>
        <TouchableOpacity
            style={[styles.signIn,{
                width:'100%',
            }]}
        >
            <LinearGradient
                colors={['#118DCD', '#118DCD']}
                style={styles.signIn}
            >
                <Text style={[styles.textSign, {color:'#fff'}]}>S'inscrire</Text>
            </LinearGradient>
        </TouchableOpacity>
        <Image
            style={styles.ciel}
            source={require('../images/ciel.png')}
            resizeMode="stretch"
        />
    </ScrollView>
);};

export default Login;

const styles = StyleSheet.create({
    container: {
      flex: 1, 
      backgroundColor: '#fff',
    },

    header: {
        marginLeft:20,
    },

    footer: {
        flex: 1,
        backgroundColor: '#fff',
        borderTopLeftRadius: 30,
        borderTopRightRadius: 30,
        paddingHorizontal: 20,
        paddingVertical: 30,
        alignItems:'center',
    },
        
    ciel:{
        marginRight:20,
    },
    
    text_header: {
        color: '#05375a',
        fontWeight: 'bold',
        fontSize: 30,
    },

    text_footer: {
        color: '#05375a',
        fontSize: 18,
    },

    action: {
        flexDirection: 'row',
        marginTop: 10,
        borderBottomWidth: 1,
        borderBottomColor: '#f2f2f2',
        paddingBottom: 5,
    },

    actionError: {
        flexDirection: 'row',
        marginTop: 10,
        borderBottomWidth: 1,
        borderBottomColor: '#FF0000',
        paddingBottom: 5
    },

    textInput: {
        flex: 1,
        marginTop: Platform.OS === 'ios' ? 0 : -12,
        paddingLeft: 10,
        color: '#05375a',
    },

    errorMsg: {
        color: '#FF0000',
        fontSize: 14,
    },
    
    button: {
        alignItems: 'center',
        marginTop: 10
    },

    signIn: {
        width: '80%',
        height: 50,
        justifyContent: 'center',
        alignItems: 'center',
        borderRadius: 10,
    },
    
    textSign: {
        fontSize: 18,
        fontWeight: 'bold'
    },
}
);
