import React,{useContext,useEffect,useState} from 'react'
import { View, Text,  StyleSheet, ScrollView, TouchableOpacity,ImageBackground,Image,Dimensions,ActivityIndicator} from 'react-native';
import Search from './Search';
import Menu1 from './Menu1'
import Dashboard from './Dashboard'
import Base from './Base'
import ButtomVoir from './ButtomVoir'
import ProgressBar from "./ProgressBar";
import {Context as AuthContext} from '../context/AuthContext';
import Header from './Header';
import Footer from './Footer';
import DateA from './DateA' ;
import { VictoryBar,VictoryAxis,VictoryVoronoiContainer ,VictoryZoomContainer, VictoryChart, VictoryTheme,VictoryLine,VictoryArea,VictoryClipContainer } from "victory-native";
import ProgressBarr from "./ProgressBarr"

console.disableYellowBox = true;

const EvatranspirationGraph=(props) =>{
  function float2int (value) {
    return value | 0;
  }
  
  const {state,signin} =useContext(AuthContext);
  const [currentDate, setCurrentDate] = useState('');
  const [currentMonth, setCurrentMonth] = useState('');
      useEffect(() => {
          setCurrentDate(
              new Date().getDate()
          );
          setCurrentMonth(
              new Date().getMonth()+1
          );     
        }, []);
  const data3 = [
    //{ quarter: 1, earnings:4},//state.reponse['data infomation']['j-7'].Pr},
    //{ quarter: 2, earnings:3},//state.reponse['data infomation']['j-6'].Pr},
    //{ quarter: 1, earnings:5},//state.reponse['data infomation']['j-5'].Pr},
    { quarter:(currentDate-5)+'/'+'0'+currentMonth, earnings:float2int(state.reponse['data infomation']['j-5'].Et0),label:state.reponse['data infomation']['j-5'].Et0},// state.reponse['data infomation']['j-4'].Date,state.reponse['data infomation']['j-4'].Pr},
    { quarter:(currentDate-4)+'/'+'0'+currentMonth, earnings:float2int(state.reponse['data infomation']['j-4'].Et0),label:state.reponse['data infomation']['j-4'].Et0},// state.reponse['data infomation']['j-3'].Date,state.reponse['data infomation']['j-3'].Pr},
    { quarter:(currentDate-3)+'/'+'0'+currentMonth, earnings:float2int(state.reponse['data infomation']['j-3'].Et0),label:state.reponse['data infomation']['j-3'].Et0},//state.reponse['data infomation']['j-2'].Date,state.reponse['data infomation']['j-2'].Pr},
    { quarter:(currentDate-2)+'/'+'0'+currentMonth, earnings:float2int(state.reponse['data infomation']['j-2'].Et0),label:state.reponse['data infomation']['j-2'].Et0},//state.reponse['data infomation']['j-1'].Date,state.reponse['data infomation']['j-1'].Pr},
    { quarter:(currentDate-1)+'/'+'0'+currentMonth, earnings:float2int(state.reponse['data infomation']['j-1'].Et0),label:state.reponse['data infomation']['j-1'].Et0},//state.reponse['data infomation']['j'].Date,state.reponse['data infomation']['j'].Pr},
    { quarter:(currentDate)+'/'+'0'+currentMonth, earnings:float2int(state.reponse['data infomation']['j'].Et0),label:state.reponse['data infomation']['j'].Et0},//state.reponse['data infomation']['j+1'].Date,state.reponse['data infomation']['j+1'].Pr},
    { quarter:(currentDate+1)+'/'+'0'+currentMonth, earnings:float2int(state.reponse['data infomation']['j+1'].Et0),label:state.reponse['data infomation']['j+1'].Et0},// state.reponse['data infomation']['j+2'].Date,state.reponse['data infomation']['j+2'].Pr},
    { quarter:(currentDate+2)+'/'+'0'+currentMonth, earnings:float2int(state.reponse['data infomation']['j+2'].Et0),label:state.reponse['data infomation']['j+2'].Et0},// state.reponse['data infomation']['j+2'].Date,state.reponse['data infomation']['j+2'].Pr},
  ];
  const [data, setData] = React.useState({
    Et0: state.reponse['data infomation']['j'].Et0,
  });  

    return (
      <View style={{flex:1}}>
        <ScrollView style={styles.container2}>
          <View>
            <Image  source={require('../images/ArriereEv.png')} style={styles.action}/>
            <Header onPress={()=>props.navigation.navigate('Evatranspiration')}/>
            <View style={styles.temView3}>
              <Text style={styles.temText1}>STATION{"\n"}{state.reponse['device infomation'].station}</Text>  
              <Image source={require('../images/Position1.png')} style={styles.Image} />
            </View>
            <View style={styles.Cont}>
              <Text style={styles.Cont1}>{state.reponse['data infomation']['j'].Date}</Text> 
              <Image source={require('../images/Et.png')} style={styles.ImageB} />
              <View>
                <Text style={styles.Cont2}>Evatranspiration</Text> 
                <Text style={styles.Cont3}>mm</Text>
              </View>
            </View>
          </View>
          <View style={styles.gra}>
            <VictoryChart 
              width={400} 
              height={400}
              theme={VictoryTheme.material}
              domainPadding={10}
              domain={{ y:[0,10]  }}
              ///domain={{  y: [0, 50] }}
             >
              <VictoryAxis
                style={{
                  ticks: {
                    padding: 5,
                  },
                  tickLabels: {
                    fontSize: 13,
                    fontWeight:'bold',
                  },
                }}
              />
              {/* Vertical */}
              <VictoryAxis
                dependentAxis
                style={{
                  ticks: {
                    // padding: 5,
                  },
                  tickLabels: {
                    fontSize: 13,
                    fontWeight:'bold',
                    color:'red'
                  },
                }}
              />
              <VictoryBar //horizontal
                barWidth={13}
                data={data3} x="quarter" y="earnings" 
                style={{ data: { fill: "#C2B49A" } }}
                //alignment="start"
                animate={{
                  duration: 2000,
                  onLoad: { duration: 1000 }
                }}
                alignment="middle"
              />
            </VictoryChart>
          </View>
        </ScrollView>
        <Footer/>
      </View>
    );
}
export default EvatranspirationGraph;
 const styles = StyleSheet.create({
  action: {
    height:"140%",
    width:"100%",
    paddingTop:20,
    position:'absolute',
    marginTop:-150
  },

  gra:{
    marginTop:"5%"
  },
  
  Cont: {
    //alignItems:'center',
    backgroundColor: 'white',
    height:110.42,
    width:"90%",
    margin:"5%",
    borderRadius: 9.29,
    marginRight:"5%",
    marginLeft:"5%",
    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 16,
    },
    shadowOpacity: 0.58,
    shadowRadius: 16.00,
    elevation: 24,
    flexDirection: 'row',
  },
 
  Cont1:{
    marginTop:"12%",
    marginLeft:"6%",
    marginRight:"-2%",
    fontFamily:"Poppins-Regular",
    fontSize:13.93,
    fontWeight:'bold',
  },

  Cont2:{
    marginTop:"26%",
    marginLeft:"-10%",
    fontWeight:'bold',
    marginRight:12,
    fontFamily:"Poppins-Regular",
    fontSize:13.93,
  },

  Cont3:{
    marginLeft:"35.5%",
    fontWeight:'bold',
    fontFamily:"Poppins-Regular",
    fontSize:18.13 ,
  },

  ImageB:{
    marginLeft:"10%",
    marginTop:"4%",
    marginRight:"4%"
  },

    text: {
      
    },

    temView2: {
      flexDirection: 'row',
      marginTop:0,
    },

    temT: {
      marginTop:25,
      alignSelf:"center",
      flexDirection: 'row',
    },

    temView3: {
      flexDirection: 'row',
    },

    temText: {
      fontSize:16,
      textAlign:"center",
      color:'#FFFFFF',
      marginBottom:10,
      fontFamily:'adobe-arabe',
    },
   
    temText4: {
      fontSize:17,
      color:'#FFFFFF',
      margin:5,
      marginLeft:20,
      fontFamily:'OpenSans-Regular'
    },
    
    temText1: {
      fontSize:30,
      color:'#FFFFFF',
      margin:10,
      marginBottom:0,
      marginLeft:20,
      fontFamily:'OpenSans-Bold',fontWeight:'bold'
    },

    temText5: {   
      marginLeft:10,
      marginRight:10,
      marginTop:120
    },

    temText6: {   
      marginLeft:60,
      marginTop:10
    },

    temText7: {   
      marginLeft:100,
      marginTop:100
    },

  container: {
    flex:1,  
  },

  container2: {
    backgroundColor:"#F8FAFC" 
  },

  Image:{
    marginLeft:60,
    marginTop:30
  },

  temText8: {
    fontSize:20,
    color:'#F15C22',
    textAlign:'center'
  },

  image1:{
    marginLeft:9,
    marginRight:40
  },
  
  image2:{
    marginLeft:35,
  },

  transform:{
    transform: [
      { rotate: "-90deg" }, 
    ],
    marginTop:"-104.5%",marginLeft:"62%"
 },

  signIn2: {
    width: 180,
    height: 40,
    justifyContent: 'center',
    alignItems: 'center',
    borderRadius: 14, //50
    flexDirection: 'row',
  },
  textSign: {
    color: 'white',
    fontWeight: 'bold',
    fontSize:19
  },
})
