import React,{Component} from 'react'
import {View,TextInput,Text,StyleSheet,Animated,Image, ImageBackground} from 'react-native'
 


class LoadingScene extends Component{

    state={
        logoAnime:new Animated.Value(0),
        loadingSpinner:false,
        
            }
            componentDidMount(){
        Animated.parallel
        
            }
render(){

    return (
    <View  style={styles.container}>
        <View style={styles.containerr}>
        <ImageBackground
             style={styles.image}
             source={require('../images/background.jpeg')}
            
        >
            <Image
            style={{width:250,height:250,margin:325}}
             source={require('../images/logoo.png')}
            />
            </ImageBackground>
        
            
        </View>
       
        
       <View  style={styles.containerrr}>

       <Text style={styles.welcome}>Cultivating ideas for growth</Text>
        <TextInput
        style={styles.input} 
        placeholder="Numéro de téléphone"       
        />
         <TextInput
        style={styles.input} 
        placeholder="Mot de passe"       
        />
        
       </View>
        
       
        
         
    </View>

    );
}


};
export default LoadingScene;

const styles=StyleSheet.create({


container:{
    flex:1,
    
},
containerr:{
    flex: 2,
    justifyContent:'center',
    alignItems:'center',


},
containerrr:{
    flex: 1,
    justifyContent:'center',
    alignItems:'center',

backgroundColor:'#2E8B57',

},
welcome:{
    fontFamily:'Lobster-Regular',
    color:'#fff',
    fontSize:20,
    textAlign:'center',
    margin:10,
    },
input:{
    borderWidth : 2,
    borderColor:"#7FFF00",
width:"90%",
backgroundColor:"#2E8B57",
padding: 15,
marginBottom: 10,
},
image:{
    alignItems:'center',

    width: '100%',
    height: '100%',
    flex: 1,
    position: 'relative',
    resizeMode: 'cover',
},


}
);
