import React,{useContext,useState,useEffect} from 'react'
import { View, Text,  StyleSheet, ScrollView,TouchableOpacity, ImageBackground,Image,Dimensions,ActivityIndicator} from 'react-native';
import Search from './Search';
import Menu1 from './Menu1'
import {Context as AuthContext} from '../context/AuthContext';
import DateA from './DateA' ;
import Header from './Header';
import Footer from './Footer';
import ButtomVoir from './ButtomVoir'

import Compasss from "./Compasss"
import {LinearGradient} from 'expo-linear-gradient';





const Vent=(props)=> {
 

  /*async componentDidMount() {
    await Font.loadAsync({
      
      'Poppins-BlackItalic': require('../assets/Fonts/Poppins-BlackItalic.ttf'),
      'OpenSans-Bold': require('../assets/Fonts/OpenSans-Bold.ttf'),
      'OpenSans-Regular': require('../assets/Fonts/OpenSans-Regular.ttf'),
      'adobe-arabe': require('../assets/Fonts/ArbFONTS-22016-adobearabic.ttf'),
      'Poppins-Bold': require('../assets/Fonts/Poppins-Bold.ttf'),
    });

    this.setState({ fontLoaded: true });
  }*/
  const {state,signin} =useContext(AuthContext);
  const [date, setDate] = React.useState({
    picture:require('../images/j1.png'),

  
    
});  
  const [currentDate, setCurrentDate] = useState('');
  const [currentMonth, setCurrentMonth] = useState('');
  useEffect(() => {
      setCurrentDate(
          new Date().getDate()
      );
      setCurrentMonth(
          new Date().getMonth()+1
      );
    
       
        
    }, []);
    const [data, setData] = React.useState({
      dateJ:state.reponse['data infomation']['j'].Date,

      Vvent: state.reponse['data infomation']['j'].Vvent,
     
      
  });  

    return (
<View  style={{flex:1}}>
<View>
      <Image  source={require('../images/ArriereVen.png')} style={styles.actionn}>
         
         </Image>
     <Header
      onPress={()=>props.navigation.navigate('Dashboard')}
     />
     </View>
      <ScrollView style={styles.container2}>
                
        <View>
        <Image  source={require('../images/ArriereVen.png')} style={styles.action}>
         
         </Image>
         
   
           <View style={styles.temView3}>
           <Text style={styles.temText1}>STATION{"\n"}{state.reponse['device infomation'].station}</Text>  
           <Image source={require('../images/Position1.png')} style={styles.Image} />
           </View>
         
        
        
          <View style={styles.Cont}>
                       <Text style={styles.Cont1}>{data.dateJ}</Text> 
                       <Image source={require('../images/Vent12.png')} style={styles.ImageB} />
   <View>
                       <Text style={styles.Cont2}>Le Vent
                       
                      
                       </Text> 
                        <Text style={styles.Cont3}>Km/h</Text>
                        </View>
                       </View>
                      
       <View  >
       <View style={styles.temView2}>
       <View style={styles.temText5}>
        
        <View>
        <View style={{flex: 1}}>
        <Image
         style={{
           alignItems:"center",
           alignContent:"center",
           marginLeft:"5%",

           
         }}
         source={date.picture}
         />
         <View style={{position: 'absolute',marginTop:"9%"}}>
           <TouchableOpacity
                  

             onPress={()=>{setData({
              dateJ:state.reponse['data infomation']['j-1'].Date,

              Vvent: state.reponse['data infomation']['j-1'].Vvent,
              

          });setDate({
            picture:require('../images/jm1.png')})}}>
         <DateA date2={(currentDate-1)+'\n'+'0'+currentMonth}  
         />
         </TouchableOpacity>
         </View>
         <View style={{position: 'absolute',marginTop:"70%"}}>
           <TouchableOpacity
            onPress={()=>{setData({
              dateJ:state.reponse['data infomation']['j'].Date,

              Vvent: state.reponse['data infomation']['j'].Vvent,
             

          });setDate({picture:require('../images/j1.png')})}}>
         <DateA date2={currentDate+'\n'+'0'+currentMonth}  
         />
         </TouchableOpacity>
         
         </View>
         <View style={{position: 'absolute',marginTop:"130%"}}>
           <TouchableOpacity
            onPress={()=>{setData({
              dateJ:state.reponse['data infomation']['j+1'].Date,

              Vvent: state.reponse['data infomation']['j+1'].Vvent,
             

          });setDate({
            picture:require('../images/jm2.png')})}}>
         <DateA date2={(currentDate+1)+'\n'+'0'+currentMonth}   
         />
         </TouchableOpacity  >
         </View>
         <View style={{position: 'absolute',marginTop:"191%"}}>
           <TouchableOpacity  
           onPress={()=>{setData({
            dateJ:state.reponse['data infomation']['j+2'].Date,

              Vvent: state.reponse['data infomation']['j+2'].Vvent,
              

          });setDate({
            picture:require('../images/jm3.png')})}}>
         <DateA date2={(currentDate+2)+'\n'+'0'+currentMonth}   
         />
         </TouchableOpacity>
         </View>
        
        

        </View>

        
        </View>
          </View>
  <View style={styles.graph}>
     <Compasss  style={styles.Compass} dataa={50*data.Vvent/100}/>
     
    

     </View>

      </View>
      <View style={styles.temText7}>
      <TouchableOpacity onPress={()=>props.navigation.navigate('VentGraph')}>
           <LinearGradient
                   colors={['#0B193C', '#0B193C']}
                   style={styles.signIn2}
           >
                    <Text style={styles.textSign}>Voir Plus</Text>
                   
               </LinearGradient>
           </TouchableOpacity>
      
        
      </View>
      </View>
</View>
      
</ScrollView>
<Footer/>
</View>
    );
  }
export default Vent;

const styles = StyleSheet.create({
  action: {
    height:"59.4%",
    width:"100%",
    paddingTop:20,
    position:'absolute',
    marginTop:"-45%"
   
 },
 actionn: {
  height:90,
  width:520,
  position:'absolute',
 
  //Swidth:240,
  alignContent:"center",
  //flex:1,


  
  }, 
 signIn2: {
  width: 180,
  height: 40,
  justifyContent: 'center',
  alignItems: 'center',
  borderRadius: 14, //50
  flexDirection: 'row',
  
},
textSign: {
  color: 'white',
  fontWeight: 'bold',
  fontSize:19
},
 graph:{
    marginLeft:"-6.5%",
    marginTop:"18%"
          },
          Cont: {
            //alignItems:'center',
            
            backgroundColor: 'white',
            height:110.42,
            width:"90%",
            margin:"5%",
            borderRadius: 9.29,
            marginTop:"10%",
            marginRight:"5%",
            marginLeft:"5%",
            marginBottom:"4%",
            shadowColor: "#000",
            shadowOffset: {
              width: 0,
              height: 16,
            },
            shadowOpacity: 0.58,
            shadowRadius: 16.00,
            
            elevation: 24,
            flexDirection: 'row',
          },
         
          Cont1:{
           
            marginTop:"11%",
            marginLeft:"6%",
            marginRight:"8%",
            fontFamily:"Poppins-Regular",
            fontSize:13.93,
            fontWeight:'bold'
            
      
          },
          Cont2:{
         
            marginTop:"35%",
            marginLeft:"0%",
            fontWeight:'bold',
      
            fontFamily:"Poppins-Regular",
            fontSize:15.93,
          },
          Cont3:{
            fontWeight:'bold',
      
            marginLeft:"18%",
            fontFamily:"Poppins-Regular",
            fontSize:15.13 ,
          },
          ImageB:{
          marginLeft:"9%",
            marginTop:"5%",
            marginRight:"8%"
          },
 text: {
     
 },
 temView2: {
    flexDirection: 'row',
    marginTop:"-16%",
   },
   temT: {
    marginTop:20,
    alignSelf:"center",
     flexDirection: 'row',
     
   },
   temView3: {
     flexDirection: 'row',
     
   },
   temText: {
     
     fontSize:16,
     textAlign:"center",
     color:'#FFFFFF',
     marginBottom:10,
     fontFamily:'adobe-arabe',
   },
  
