import React,{Component,useState,useContext,useEffect} from 'react';
import { 
    View, 
    Text, 
    TouchableOpacity, 
    TextInput,
    Platform,
    StyleSheet ,
    Dimensions,
    StatusBar,
    Alert,
    Image,
    Linking,
    BackHandler
} from 'react-native';

import {
    widthPercentageToDP as wp,
    heightPercentageToDP as hp,
    listenOrientationChange as loc,
    removeOrientationListener as rol
  } from 'react-native-responsive-screen';
import {Context as AuthContext} from '../context/AuthContext';
//import {Context1 as Alldevice} from '../context/Alldevice';

import * as Animatable from 'react-native-animatable';
console.disableYellowBox = true;
import {Card} from 'react-native-shadow-cards';
import Footer from './Footer';

import {LinearGradient} from 'expo-linear-gradient';
import FontAwesome from 'react-native-vector-icons/FontAwesome';
import MaterialIcons from 'react-native-vector-icons/MaterialIcons';

import Feather from 'react-native-vector-icons/Feather';
import Langue2 from './Langue2';
import { set } from 'react-native-reanimated';
import AuthentificationAPI from '../API/AuthentificationAPI';
import {getDATAFromApi} from '../API/DATAAPI';
import { ScrollView } from 'react-native-gesture-handler';

const Login=(props) => {

    const handleWebPress =() => {
        Linking.openURL("http://www.yobeen.com");
    }
    const[results,setResults]=React.useState([]);

    const {state,signin} =useContext(AuthContext);
    const [data, setData] = React.useState({
        
        username: '',
        password: '',
        check_textInputChange: false,
        secureTextEntry: true,
        isValidUser: true,
        isValidPassword: true,
    });
    const handlePasswordChange = (val) => {
        if( val.trim().length >= 8 ) {
            setData({
                ...data,
                password: val,
                isValidPassword: true
            });
        } else {
            setData({
                ...data,
                password: val,
                isValidPassword: false
            });
        }
    
    }

    const updateSecureTextEntry = () => {
        setData({
            ...data,
            secureTextEntry: !data.secureTextEntry
        });
    }

    const textInputChange = (val) => {
        if( val.trim().length == 10  ) {
            setData({
                ...data,
                username: val,
                check_textInputChange: true,
            });
        } else {
            setData({
                ...data,
                username: val,
                check_textInputChange: false,
            });
        }
    }
 
        var message="Ce numéro de téléphone n'est pas valide";
const erreur1=() =>{
        if (state.reponse['user infomation'].length==0)
        {
Alert.alert('OOPS!','Ce numéro de téléphone n est pas valide',[
    {text:'OK',onPress:()=>console.log('alert closed')}
])
        };
    }
    const erreur2=() =>{
        if(state.reponse.message_info=="The phone number is not valid")
        {
            Alert.alert('OOPS!','Veuillez saisir un numéro de téléphone de 10 chiffres',[
                {text:'OK',onPress:()=>console.log('alert closed')}
            ])
                    }
        else{
            erreur1()

                    }
                }
                
                   
                 componentDidMount=()=>
                {
                     BackHandler.addEventListener('hardwareBackPress',handleBackButtonClick())
                }
                 componentWillUnmount=()=>
                {
                    BackHandler.removeEventListener('hardwareBackPress',handleBackButtonClick())
                }
                handleBackButtonClick=()=> {
                    props.navigation.goBack(null);
                    return true;
                }                  
               
       

    return (
        <View style={{flex:1}}>
            <View>
            <Image
        style={styles.header}
            
            source={require('../images/green.png')}
           
           resizeMode="stretch"
           />
           
           <Langue2 />  

            </View>
    <View style={styles.container}>
        
       
        
        <View  style={styles.containerr}>
            <LinearGradient
                    colors={['#fff', '#fff']}
                    style={[styles.signIn, {
                        borderColor: '#7FD0D7',
                        borderWidth: 2,
                        //marginTop:"19%",
                        borderRadius: 50,
                       
                        marginBottom:hp("1%") 
                    }]}
                >
                     <View style={styles.action}>
                     <Image
         style={{marginLeft:"5%"}}
            source={require('../images/tlphn.png')}
           
          
           />

                
                 <TextInput 
                    placeholder="Entrez votre numéro de téléphone"
                    placeholderTextColor="#7FD0D7"
                    style={styles.textInput}
                    autoCapitalize="none"
                    keyboardType="numeric"
                    underlineColorAndroid="transparent"
                    value={data.username}
                    onChangeText={(val) => textInputChange(val)
                
                    }

                />
                {data.check_textInputChange ? 
                 <Image
                 style={{marginRight:20,width:30,height:30}}
                    source={require('../images/verifier.png')}
                   
                  
                   />:null}
            </View>
            </LinearGradient>
           
            {state.reponse.message_info? <Text style={styles.errorMsg}>Ce numéro de téléphone est invalide</Text> :<Text></Text>}
     
                <TouchableOpacity
               
                onChange={()=>state.reponse.message_info=='' }
                onPress={()=>{signin(data.username)}}/*()=>props.navigation.navigate('CountDownn')*/
                    style={[styles.signInn,{
                        width:'100%',
                                        }]}
                    
                >
                <LinearGradient
                    colors={['#118DCD', '#118DCD']}
                    style={styles.signIn}
                >
                    <Text style={[styles.textSign, {
                        color:'#fff'
                    }]}>Se connecter</Text>
                </LinearGradient>
                </TouchableOpacity>
                
               
           </View>
           
     

</View>
  <View>        
<View >
            <Image
             style={styles.ciel}
            source={require('../images/ciel.png')}
           
           resizeMode="stretch"
           />
</View>
         
           <Footer/>
           </View> 
</View>
);
};

export default Login;
const {height} = Dimensions.get("screen");


const styles = StyleSheet.create({
    container: {
     flex: 1, 
      backgroundColor: '#fff',
      marginTop:hp("2%"),
      //alignItems:'center',
      //marginTop:30,
      height
    },
    containerr:{
        alignItems:'center',
        alignContent:'center',
    },
    header: {
      // marginBottom:"15%",
            marginLeft:wp("6%"),
        
        
        
    },
    image1:{
        width:50,
        height:50,
        marginLeft:"45%",
     
      },
    footer: {
        
        flex: 1,
        backgroundColor: '#fff',
        borderTopLeftRadius: 30,
        borderTopRightRadius: 30,
        paddingHorizontal: 20,
        paddingVertical: 30,
        alignItems:'center',
        
    },
    
        
        ciel:{
        
            //marginRight:"20%",
            marginBottom:hp("-2.3%"),
            backgroundColor:'transparent'
    
            },
    
    text_header: {
        color: '#05375a',
        fontWeight: 'bold',
        fontSize: 30,
    },
    text_footer: {
        color: '#05375a',
        fontSize: 18,
    },
    foot:{
fontSize:16,
color:'#f5af19',
fontWeight:'bold'
    },
    yobeen:{
        flexDirection:'row',
        marginLeft:"-55%" ,
        marginTop:"85%"  ,
        marginRight:"3%",
       },
      
    action: {
        flexDirection: 'row',
        marginTop: hp('1.5%'),
        borderBottomWidth: 1,
        borderBottomColor: '#f2f2f2',
        paddingBottom: 5,
 
    },
  
    
    actionError: {
        flexDirection: 'row',
        marginTop:"4%",
        borderBottomWidth: 1,
        borderBottomColor: '#FF0000',
        paddingBottom: 5
    },
    textInput: {
        flex: 1,
        marginTop: Platform.OS === 'ios' ? 0 : "-1.4%",
        paddingLeft: 10,
        color: '#05375a',
        //borderBottomColor: 'transparent',

        
    },
    errorMsg: {
        color: '#FF0000',
        fontSize: 14,
        marginBottom:"2%",
        fontWeight:'bold'
    },
    
    button: {
        alignItems: 'center',
        marginTop:"4%"
    },
    signIn: {
        width: '80%',
        height: 50,
        justifyContent: 'center',
        alignItems: 'center',
        borderRadius: 10,
        
    },
    signInn: {
        width: '80%',
        height: 50,
        justifyContent: 'center',
        alignItems: 'center',
        borderRadius: 10,
        marginBottom:"32%"
        
    },
    textSign: {
        fontSize: 18,
        fontWeight: 'bold'
    },
   
}
);
