import React,{Component} from 'react'
import { View, 
    Text, 
    Image,
    StyleSheet,
    ImageBackground
    
} from 'react-native';
import FontAwesome from 'react-native-vector-icons/FontAwesome';
import MaterialIcons from 'react-native-vector-icons/MaterialIcons';
console.disableYellowBox = true;



import { MenuProvider } from 'react-native-popup-menu';
import {
    Menu,
    MenuOptions,
    MenuOption,
    MenuTrigger,
  } from 'react-native-popup-menu';
const Langue=(props) =>{

   


    return (

        <ImageBackground  source={require('../images/green.png')} style={styles.header}>
        <MenuProvider>
        
        <Menu>
          <MenuTrigger>
            <Image  
                source={require('../images/barr.png')}
                style={{marginLeft:300,marginTop:45}}

            />
     
                        
                    
        </MenuTrigger> 
       
          <MenuOptions style={styles.menu}>
        
            <Text style={styles.titre}>
           
            Langue
                </Text>
                <MenuOption>
              <Text  style={styles.options} >       Arabe</Text>
            </MenuOption>
            <MenuOption>
              <Text  style={styles.options} >       Fran√ßais</Text>
            </MenuOption>
            <MenuOption>
              <Text  style={styles.options} >       Anglais</Text>
            </MenuOption>
            <MenuOption>
              <Text style={styles.titre} >
             
                  Contactez-nous</Text>
            </MenuOption>
            
          </MenuOptions>
          
        </Menu>
  
      </MenuProvider>

      </ImageBackground>
    );
}


;
export default Langue;
const styles = StyleSheet.create({
    options:{
         
      fontSize:16, 
         },
         menu:{
            marginTop:29,backgroundColor:'white',width:280,paddingLeft:20,borderRadius:23,borderWidth:1,borderColor:'#283c86'
          
         },
         titre:{color: '#118DCD',fontWeight:'bold',fontSize:20},
         header: {
            flex: 1,
            justifyContent: 'center',
            alignItems: 'center',
              },
       

   
  
},
);
