import React,{useContext,useEffect,useState} from 'react'
import { 
  View, 
  Text,  
  StyleSheet, 
  ScrollView, 
  TouchableOpacity,
  ImageBackground,
  Image,
  Dimensions,
  ActivityIndicator
} from 'react-native';
import { ScaledSheet } from 'react-native-size-matters';
import Search from './Search';
import Menu1 from './Menu1'
import Dashboard from './Dashboard'
import Base from './Base'
import ButtomVoir from './ButtomVoir'
import ProgressBar from "./ProgressBar";
import {Context as AuthContext} from '../context/AuthContext';
import Header from './Header';
import Footer from './Footer';
import DateA from './DateA' ;
console.disableYellowBox = true;
import {LinearGradient} from 'expo-linear-gradient';
import ProgressBarr from "./ProgressBarr"

const Evatranspiration =(props) =>{
  const [date, setDate] = React.useState({
    picture:require('../images/j1.png'), 
  });  
  const {state,signin} =useContext(AuthContext);
  const [currentDate, setCurrentDate] = useState('');
  const [currentMonth, setCurrentMonth] = useState('');
  useEffect(() => {
    setCurrentDate(
      new Date().getDate()
    );
    setCurrentMonth(
      new Date().getMonth()+1
    );
  }, []);
  const [data, setData] = React.useState({
    Et0: state.reponse['data infomation']['j'].Et0,
    dateJ:state.reponse['data infomation']['j'].Date,
  });  

    return (
      <View style={{flex:1}}>
        <ScrollView style={styles.container2}>
          <View>
            <Image  source={require('../images/ArriereEv.png')} style={styles.action}/>
            <Header onPress={()=>props.navigation.navigate('Dashboard')}/>
            <View style={styles.temView3}>
              <Text style={styles.temText1}>STATION{"\n"}{state.reponse['device infomation'].station}</Text>  
              <Image source={require('../images/Position1.png')} style={styles.Image} />
            </View>
            <View style={styles.Cont}>
              <Text style={styles.Cont1}>{data.dateJ}</Text> 
              <Image source={require('../images/Et.png')} style={styles.ImageB} />
              <View>
                <Text style={styles.Cont2}>Evatranspiration</Text> 
                <Text style={styles.Cont3}>mm</Text>
              </View>
            </View>
            <View>
              <View style={styles.temView2}>
                <View style={styles.temText5}>
                  <View>
                    <View style={{flex: 1}}>
                      <Image
                        style={{
                          alignItems:"center",
                          alignContent:"center",
                          marginLeft:"5%",
                        }}
                        source={date.picture}
                      />
                      <View style={{position: 'absolute',marginTop:"7%"}}>
                        <TouchableOpacity
                          onPress={()=>{
                            setData({
                              dateJ:state.reponse['data infomation']['j-1'].Date,
                              Et0: state.reponse['data infomation']['j-1'].Et0,
                            });
                            setDate({
                              picture:require('../images/jm1.png')
                            })
                          }}
                        >
                          <DateA date2={(currentDate-1)+'\n'+'0'+currentMonth} />
                        </TouchableOpacity>
                      </View>
                      <View style={{position: 'absolute',marginTop:"70%"}}>
                        <TouchableOpacity
                          onPress={()=>{
                            setData({
                              dateJ:state.reponse['data infomation']['j'].Date,
                              Et0: state.reponse['data infomation']['j'].Et0,
                            });
                            setDate({
                              picture:require('../images/j1.png')
                            })
                          }}
                        >
                          <DateA date2={currentDate+'\n'+'0'+currentMonth}/>
                        </TouchableOpacity>
                      </View>
                      <View style={{position: 'absolute',marginTop:"130%"}}>
                        <TouchableOpacity
                          onPress={()=>{
                            setData({
                              dateJ:state.reponse['data infomation']['j+1'].Date,
                              Et0: state.reponse['data infomation']['j+1'].Et0,
                            });
                            setDate({
                              picture:require('../images/jm2.png')
                            })
                          }}
                        >
                          <DateA date2={(currentDate+1)+'\n'+'0'+currentMonth}/>
                        </TouchableOpacity  >
                      </View>
                      <View style={{position: 'absolute',marginTop:"191%"}}>
                        <TouchableOpacity  
                          onPress={()=>{
                            setData({
                              dateJ:state.reponse['data infomation']['j+2'].Date,
                              Et0: state.reponse['data infomation']['j+2'].Et0,
                            });
                            setDate({
                              picture:require('../images/jm3.png')
                            })
                          }}
                        >
                          <DateA date2={(currentDate+2)+'\n'+'0'+currentMonth}/>
                        </TouchableOpacity>
                      </View>
                    </View>
                  </View>
                </View>
                <ProgressBarr dataa={data.Et0} />
                <Text style={{marginTop:"88%",position:'absolute',marginLeft:"50%",fontWeight:'bold',color:'black',fontSize:18}}>{data.Et0+"mm"}</Text> 
              </View>
              <View style={styles.temText7}>
                <TouchableOpacity onPress={()=>props.navigation.navigate('EvatranspirationGraph')}>
                  <LinearGradient
                    colors={['#0B193C', '#0B193C']}
                    style={styles.signIn2}
                  >
                    <Text style={styles.textSign}>Voir Plus</Text>
                  </LinearGradient>
                </TouchableOpacity>
              </View>
            </View>
          </View>
        </ScrollView>
        <Footer/>
      </View>
    );
}

export default Evatranspiration;

const styles = StyleSheet.create({
  action: {
    height:"48%",
    width:"100%",
    paddingTop:20,
    position:'absolute',
    marginTop:-100
  },
 
  Cont: {
    //alignItems:'center',
    backgroundColor: 'white',
    height:"15%",
    width:"90%",
    margin:"5%",
    borderRadius: 9.29,
    marginBottom:"6%",
    marginRight:"5%",
    marginLeft:"5%",
    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 16,
    },
    shadowOpacity: 0.58,
    shadowRadius: 16.00,
    elevation: 24,
    flexDirection: 'row',
  },

  Cont1:{
    marginTop:"12%",
    marginLeft:"6%",
    marginRight:"-2%",
    fontFamily:"Poppins-Regular",
    fontSize:13.93,
    fontWeight:'bold',
  },

  Cont2:{
    marginTop:"26%",
    marginLeft:"-10%",
    fontWeight:'bold',
    marginRight:"4%",
    fontFamily:"Poppins-Regular",
    fontSize:13.93,
  },

  Cont3:{
    marginLeft:"35.5%",
    fontWeight:'bold',
    fontFamily:"Poppins-Regular",
    fontSize:18.13 ,
  },

  ImageB:{
    marginLeft:"10%",
    marginTop:"4%",
    marginRight:"4%"
  },

  text: {
     
  },

  temView2: {
    flexDirection: 'row',
    marginTop:0,
  },
  
  temView3: {
     flexDirection: 'row',
  },

  temText: {
    fontSize:16,
    textAlign:"center",
    color:'#FFFFFF',
    marginBottom:10,
    fontFamily:'adobe-arabe',
  },
  
  temText4: {
    fontSize:17,
    color:'#FFFFFF',
    margin:5,
    marginLeft:20,
    fontFamily:'OpenSans-Regular'
  },

  temText1: {
    fontSize:30,
    color:'#FFFFFF',
    margin:10,
    marginBottom:0,
    marginLeft:20,
    fontFamily:'OpenSans-Bold',fontWeight:'bold'
  },

  temText5: {   
    marginLeft:"5%",
    marginRight:"2.4%",
    marginTop:"20%"
   },

  temText6: {   
    marginLeft:60,
    marginTop:10
  },

  temText7: {   
    marginLeft:"30%",
    marginTop:"22%",
    height:86  
  },

  signIn2: {
    width: 180,
    height: 40,
    justifyContent: 'center',
    alignItems: 'center',
    borderRadius: 14, //50
    flexDirection: 'row',
  },

  textSign: {
    color: 'white',
    fontWeight: 'bold',
    fontSize:19
  },

  container: {
    flex:1,  
  },

  container2: {
    backgroundColor:"#F8FAFC" 
  },

  Image:{
    marginLeft:60,
    marginTop:30
  },

  temText8: {
    fontSize:20,
    color:'#F15C22',
    textAlign:'center'
  },

  image1:{
    marginLeft:9,
    marginRight:40
  },
 
  image2:{
    marginLeft:35,
  },

})
