import React,{Component} from 'react';
import { 
    View, 
    Text,
    StyleSheet ,
    Image,
    Linking
   
} from 'react-native';
import { Colors }from 'react-native/Libraries/NewAppScreen';

import FontAwesome from 'react-native-vector-icons/FontAwesome';
import MaterialIcons from 'react-native-vector-icons/MaterialIcons';
console.disableYellowBox = true;

import {
  widthPercentageToDP as wp,
  heightPercentageToDP as hp,
  listenOrientationChange as loc,
  removeOrientationListener as rol
} from 'react-native-responsive-screen';

import { MenuProvider } from 'react-native-popup-menu';
import {
    Menu,
    MenuOptions,
    MenuOption,
    MenuTrigger,
  } from 'react-native-popup-menu';
import { TouchableOpacity } from 'react-native-gesture-handler';

const nom ="safae";
  const Langue2=(props) =>{

   
const handleEmailPress =() => {
  Linking.openURL("mailto:agrotech@yobeen.com?subject=Email from the Agro tech mobile application  &body='Bonjour!'");

}
const handleWebPress =() => {
  Linking.openURL("https://google.com");

}
const handleCallPress =() => {
  Linking.openURL("tel:+212660111666");

}


const handleSmsPress =() => {
  Linking.openURL("sms:+212660111666");

}
//meme numero pour wtsp 


    return (

<View style={{position: 'absolute',flex: 1,alignItems: 'center',justifyContent: 'center',}}>
           <MenuProvider style={{height:hp("35%"),marginTop:hp("6%"),marginLeft:wp("12%")}}>
        
        <Menu >
          <MenuTrigger>
          <Image  
                source={require('../images/barr.png')}
                style={{marginLeft:wp("70.6%"),marginTop:hp("0%")}}
            />
         
        </MenuTrigger> 
       
          <MenuOptions style={styles.menu}>
            
            
           
            <MenuOption>

              <Text style={styles.titre} >
              
                  Contactez-nous</Text>
                  <View   style={{marginLeft:wp("3%")}}>
                  <TouchableOpacity   onPress={handleEmailPress}>
                  <MenuOption>
                  <Image source={require('../images/email.png')} style={{position:'absolute',marginTop:5}}/>
<Text style={{fontWeight:'bold',color:'#118DCD',marginLeft:6}}>G</Text>
              <Text  style={{fontSize:wp('4%'),position:'absolute',marginLeft:wp('2%'),marginTop:3}} >       Email</Text>
              
            </MenuOption>
            </TouchableOpacity>

            <TouchableOpacity   onPress={handleCallPress}>
            <MenuOption>
            <Image source={require('../images/tlphn.png')} style={{position:'absolute'}}/>

              <Text  style={styles.options} >       Appel</Text>
            </MenuOption>
            </TouchableOpacity>
            <TouchableOpacity   onPress={handleSmsPress}>
            <MenuOption>
            <Image source={require('../images/email.png')} style={{position:'absolute',marginTop:5}}/>

              <Text  style={styles.options} >       Sms</Text>
            </MenuOption>
            </TouchableOpacity>
            </View>
            </MenuOption>
            
          </MenuOptions>
          
        </Menu>
  
      </MenuProvider>

        
         </View>
             
             );
            }
            
            
            ;
            export default Langue2;
            const styles = StyleSheet.create({
                options:{
         
                    fontSize:wp('4%'), 
                       },
                       menu:{
                          marginLeft:"-9%",marginTop:0,width:wp('66%'),paddingLeft:25,borderRadius:30,backgroundColor:'white',borderColor:'#283c86',borderWidth:1
                        
                       },
                       titre:{color: '#118DCD',fontWeight:'bold',fontSize:wp('4.5%')},
            
               
              
            },
            );
