import React,{useContext,useState,useEffect} from 'react'
import { View, Text,  StyleSheet, ScrollView, ImageBackground,Image,Dimensions} from 'react-native';
import Search from './Search';
import Pluie from './Pluie';
import Header from './Header';
import Footer from './Footer';
import {Context as AuthContext} from '../context/AuthContext';
import DateA from './DateA' ;
import Modell from './Modell';
import ButtomVoir from './ButtomVoir'
import { TouchableOpacity } from 'react-native-gesture-handler';
import { VictoryBar,VictoryAxis, VictoryChart, VictoryTheme,VictoryArea,VictoryClipContainer } from "victory-native";

const PrecipitationGraphe=(props) =>{
  const {width, height} = Dimensions.get('window')
  const {state,signin} =useContext(AuthContext);
  const [currentDate, setCurrentDate] = useState('');
  const [currentMonth, setCurrentMonth] = useState('');
      useEffect(() => {
          setCurrentDate(
              new Date().getDate()
          );
          setCurrentMonth(
              new Date().getMonth()+1
          );
        
           
            
        }, []);
          const data3 = [
    //{ quarter: 1, earnings:4},//state.reponse['data infomation']['j-7'].Pr},
    //{ quarter: 2, earnings:3},//state.reponse['data infomation']['j-6'].Pr},
    //{ quarter: 1, earnings:5},//state.reponse['data infomation']['j-5'].Pr},
    { quarter:(currentDate-5)+'/'+'0'+currentMonth, earnings:state.reponse['data infomation']['j-4'].Pr? state.reponse['data infomation']['j-5'].Pr : 0,label:state.reponse['data infomation']['j'].Pr? state.reponse['data infomation']['j-5'].Pr : 0},
    { quarter:(currentDate-4)+'/'+'0'+currentMonth, earnings:state.reponse['data infomation']['j-3'].Pr? state.reponse['data infomation']['j-4'].Pr : 0,label:state.reponse['data infomation']['j-4'].Pr? state.reponse['data infomation']['j-4'].Pr : 0},
    { quarter:(currentDate-3)+'/'+'0'+currentMonth, earnings:state.reponse['data infomation']['j-2'].Pr? state.reponse['data infomation']['j-3'].Pr : 0,label:state.reponse['data infomation']['j-3'].Pr? state.reponse['data infomation']['j-3'].Pr : 0},
    { quarter:(currentDate-2)+'/'+'0'+currentMonth, earnings:state.reponse['data infomation']['j-1'].Pr? state.reponse['data infomation']['j-2'].Pr : 0,label:state.reponse['data infomation']['j-2'].Pr? state.reponse['data infomation']['j-2'].Pr : 0},
    { quarter:(currentDate-1)+'/'+'0'+currentMonth, earnings:state.reponse['data infomation']['j'].Pr? state.reponse['data infomation']['j-1'].Pr : 0,label:state.reponse['data infomation']['j-1'].Pr? state.reponse['data infomation']['j-1'].Pr : 0},
    { quarter:(currentDate)+'/'+'0'+currentMonth, earnings:state.reponse['data infomation']['j+1'].Pr? state.reponse['data infomation']['j'].Pr : 0,label:state.reponse['data infomation']['j'].Pr? state.reponse['data infomation']['j'].Pr : 0},
    { quarter:(currentDate+1)+'/'+'0'+currentMonth, earnings:state.reponse['data infomation']['j+2'].Pr? state.reponse['data infomation']['j+1'].Pr : 0,label:state.reponse['data infomation']['j+1'].Pr? state.reponse['data infomation']['j+1'].Pr : 0},
    { quarter:(currentDate+2)+'/'+'0'+currentMonth, earnings:state.reponse['data infomation']['j+2'].Pr? state.reponse['data infomation']['j+2'].Pr : 0,label:state.reponse['data infomation']['j+2'].Pr? state.reponse['data infomation']['j+2'].Pr : 0},

  ];
 
    return (
      <View style={{flex:1}}>
<ScrollView  style={styles.container}>
  
  
<Image  source={require('../images/ArrierePr.png')} style={styles.action}>
         
         </Image>
         <Header
      onPress={()=>props.navigation.navigate('precipitation')}
     />
           <View style={styles.temView3}>
           <Text style={styles.temText1}>STATION{"\n"}{state.reponse['device infomation'].station}</Text>  
           <Image source={require('../images/Position1.png')} style={styles.Image} />
           </View>
   <View style={styles.Cont}>
                    <Text style={styles.Cont1}>{state.reponse['data infomation']['j'].Date}</Text> 
                    <Image source={require('../images/petit-nuage.png')} style={styles.ImageB} />
<View>
                    <Text style={styles.Cont2}>Précipitation
                    
                   
                    </Text> 
                     <Text style={styles.Cont3}>mm</Text>
                     </View>
                    </View>

                    <View style={styles.gra}>
        <VictoryChart 
        width={400} 
        height={400}
        theme={VictoryTheme.material}
        domainPadding={10}
        ///domain={{  y: [0, 50] }}
        >
          <VictoryAxis
    
    style={{
        ticks: {
            padding: 5,
        },
        tickLabels: {
            fontSize: 13,
            fontWeight:'bold',
            
        },
    }}
/>
{/* Vertical */}
<VictoryAxis
    dependentAxis
    style={{
      ticks: {
         // padding: 5,
      },
      tickLabels: {
          fontSize: 13,
          fontWeight:'bold',
          color:'red'
      },
  }}
/>
          <VictoryBar //horizontal
          barWidth={({ index }) => index * 2 + 10}
          data={data3} x="quarter" y="earnings" 
          style={{ data: { fill: "#4DC5DB" } }}
          //alignment="start"
          animate={{
            duration: 2000,
            onLoad: { duration: 1000 }
          }}
          alignment="middle"
          
          />
        </VictoryChart>
      
      </View>

   </ScrollView>
   <Footer/>
   </View>
    
    );
};
export default PrecipitationGraphe;
const styles = StyleSheet.create({
    action: {
      height:"60%",
      width:"100%",
      paddingTop:20,
      position:'absolute',
      marginTop:-155
    },
    text: {
        
    },
    temView2: {
        flexDirection: 'row',
        marginTop:90
      },
      temT: {
        marginTop:"6%",
        alignSelf:"center",
        flexDirection: 'row',
        
      },
      temView3: {
        flexDirection: 'row',
        
      },
      temText: {
        
        fontSize:16,
        textAlign:"center",
        color:'#FFFFFF',
        marginBottom:10,

      },
      temText4: {
       
        fontSize:17,
        color:'#FFFFFF',
        margin:5,
        marginLeft:20

      },
      Cont: {
        //alignItems:'center',
        
        backgroundColor: 'white',
        height:110.42,

        width:"90%",
        margin:"5%",
        borderRadius: 9.29,
        marginRight:"5%",
        marginLeft:"5%",
        shadowColor: "#000",
        shadowOffset: {
          width: 0,
          height: 16,
        },
        shadowOpacity: 0.58,
        shadowRadius: 16.00,
        
        elevation: 24,
        flexDirection: 'row',
      },
     
      Cont1:{
       
        marginTop:41,
        marginLeft:10,
        marginRight:0,
        fontFamily:"Poppins-Regular",
        fontSize:13.93,
        fontWeight:'bold'
        
  
      },

      Cont2:{
        fontWeight:'bold',

        marginTop:41,
        marginLeft:5,
       
        fontFamily:"Poppins-Regular",
        fontSize:13.93,
      },
      Cont3:{
        fontWeight:'bold',

        marginLeft:52,
        fontFamily:"Poppins-Regular",
        fontSize:20.13 ,
      },
      ImageB:{
      marginLeft:34,
        marginTop:28,
        marginRight:39
      },
      temText1: {
       
        fontSize:30,
        color:'#FFFFFF',
        margin:10,
        marginLeft:20,
fontWeight:'bold',
        marginBottom:0

      },
      temText5: {   
        marginLeft:10,
        marginRight:10,
        
      },
      imagei:{
        //marginLeft:"13.4%",
        width:22,
        height:22,
       // marginRight:"-20%"
      },
      temText6: {   
        marginLeft:5,
        marginTop:50
       
       },
       
       temText7: {   
        marginLeft:100,
        marginTop:100
       
       },
    container: {
        flex:1,  
        backgroundColor:'#fff'
       
    },
    Image:{
      marginLeft:60,
      marginTop:30
     
    },
    temText8: {
       
      fontSize:20,
      color:'#F15C22',
      
     textAlign:'center'

    },
    image1:{
     marginLeft:"4%",
     marginRight:"5%"
   },
    image2:{
      marginLeft:35,
     
    },
    gra: {
      //flex: 1,
      justifyContent: "center",
      alignItems: "center",
      marginLeft:10
      //backgroundColor: "#f5fcff"
    }
  

}
);
