import React,{useContext, useState, useEffect } from 'react'
import { View, Text,  StyleSheet, ScrollView, Image} from 'react-native';
import Header from './Header';
import Footer from './Footer';
import {Context as AuthContext} from '../context/AuthContext';
import { TouchableOpacity } from 'react-native-gesture-handler';
import { VictoryBar,VictoryZoomContainer ,VictoryAxis, VictoryChart,VictoryLabel,VictoryTheme } from "victory-native";
import {
  widthPercentageToDP as wp,
  heightPercentageToDP as hp,
  listenOrientationChange as loc,
  removeOrientationListener as rol
} from 'react-native-responsive-screen';
import { ScaledSheet } from 'react-native-size-matters';

const RayonnementGraphe=(props) =>{
  const {state,signin} =useContext(AuthContext);
  const [currentDate, setCurrentDate] = useState('');
  const [currentMonth, setCurrentMonth] = useState('');
    useEffect(() => {
      setCurrentDate(
        new Date().getDate()
      );
      setCurrentMonth(
        new Date().getMonth()+1
      );
    }, []);

    function float2int (value) {
      return value | 0;
    }

  const data3 = [
    { quarter:(currentDate-5)+'/'+'0'+currentMonth, earnings:float2int(state.reponse['data infomation']['j-5'].Rg),label:state.reponse['data infomation']['j-5'].Rg},
    { quarter:(currentDate-4)+'/'+'0'+currentMonth, earnings:float2int(state.reponse['data infomation']['j-4'].Rg),label:state.reponse['data infomation']['j-4'].Rg},
    { quarter:(currentDate-3)+'/'+'0'+currentMonth, earnings:float2int(state.reponse['data infomation']['j-3'].Rg),label:state.reponse['data infomation']['j-3'].Rg},
    { quarter:(currentDate-2)+'/'+'0'+currentMonth, earnings:float2int(state.reponse['data infomation']['j-2'].Rg),label:state.reponse['data infomation']['j-2'].Rg},
    { quarter:(currentDate-1)+'/'+'0'+currentMonth, earnings:float2int(state.reponse['data infomation']['j-1'].Rg),label:state.reponse['data infomation']['j-1'].Rg},
    { quarter:(currentDate)+'/'+'0'+currentMonth,   earnings:float2int(state.reponse['data infomation']['j'].Rg),label:state.reponse['data infomation']['j'].Rg},
    { quarter:(currentDate+1)+'/'+'0'+currentMonth, earnings:float2int(state.reponse['data infomation']['j+1'].Rg),label:state.reponse['data infomation']['j+1'].Rg},
    { quarter:(currentDate+2)+'/'+'0'+currentMonth, earnings:float2int(state.reponse['data infomation']['j+2'].Rg),label:state.reponse['data infomation']['j+2'].Rg},
  ];

    return (
      <View style={styles.container}>
        <Image  source={require('../images/ArruereR.png')} style={styles.imageBackground}/>
        <View style={styles.header}>
          <Header onPress={()=>props.navigation.navigate('menu')}/>
        </View>
        <ScrollView  style={styles.scrollContainer}>         
          <View style={styles.stationEtPosition}>
            <Text style={styles.stationText}>STATION{"\n"}{state.reponse['device infomation'].station}</Text>  
            <Image source={require('../images/Position1.png')} style={styles.imagePosition} />
          </View>
          <View style={styles.dateEtRayonnement}>
            <Text style={styles.date}>{state.reponse['data infomation']['j'].Date}</Text> 
            <Image source={require('../images/soleil.png')} style={styles.imageRayonnement} />
            <View style={{ flexDirection:'column' }}>
              <Text style={styles.rayonnementText}>Rayonnement</Text> 
              <Text style={styles.unitText}>J/cm²</Text>
            </View>
          </View>
          <View style={styles.graph}>
            <VictoryChart 
              width={ '410@s' } 
              height={ '400@vs' }
              theme={VictoryTheme.material}
              domainPadding={10}
              domain={{}}
            >
              <VictoryBar 
                horizontal
                barWidth={ '13@s' }
                data={data3} x="quarter" y= "earnings"
                style={{ data: { fill: "#F0CB35" } }}
                //alignment="start"
                animate={{
                  duration: 2000,
                  onLoad: { duration: 1000 }
                }}
                alignment="middle"
                labelComponent={
                  <VictoryLabel verticalAnchor="middle"
                  dy={ '-20@s'}
                  dx={ '28@s' } textAnchor="end"/>
                }
              />
              <VictoryAxis
                style={{
                  tickLabels: {fontSize: 14 , padding: '5@s',color:"transparent",fontWeight:'bold'}
                }}
              />
            </VictoryChart>
          </View>
        </ScrollView>
        <Footer style={styles.footer}/>
      </View>
    );
};

export default RayonnementGraphe;

const styles = ScaledSheet.create({
    
    container:{
      flex: 1,
      flexDirection: 'column',
    },

    header:{
      flex: 1,
      flexDirection: 'row'
    },

    scrollContainer: {
      flex: 8,  
      flexDirection: 'column',
      backgroundColor:'#fff'
    },

    imageBackground: {
      height: wp("55%"),
      width: hp("100%"),
      paddingTop: '40@vs',
      position:'absolute',
      marginTop: '-143@vs'
    },
    
    stationEtPosition: {
      flex: 1,
      flexDirection: 'row',
    },
    
    stationText: {
      fontSize: 30,
      color:'#FFFFFF',
      margin: '10@s',
      marginLeft: '20@s',
      fontWeight:'bold',
      marginBottom: '0@vs'
    },

    imagePosition:{
      marginLeft: '60@s',
      marginTop: '30@vs'
    },

    dateEtRayonnement: {
      flex: 1,
      flexDirection: 'row',
      //alignItems:'center',
      backgroundColor: 'white',
      height: '110.42@vs',
      width: wp("90%"),
      margin: wp("5%"),
      borderRadius: 9.29,
      marginRight: wp("5%"),
      marginLeft: wp("5%"),
      shadowColor: "#000",
      shadowOffset: {
        width: 0,
        height: 16,
      },
      shadowOpacity: 0.58,
      shadowRadius: 16.00,
      elevation: 24,
      flexDirection: 'row',
    },
    
    date:{
      marginTop: hp("12%"),
      marginLeft: wp("6%"),
      marginRight: wp("-2%"),
      fontFamily:"Poppins-Regular",
      fontSize: 13.9,
      fontWeight:'bold',
    },

    rayonnementText:{
      marginTop: hp("26%"),
      marginLeft: wp("5%"),
      fontWeight:'bold',
      marginRight: '12@s',
      fontFamily:"Poppins-Regular",
      fontSize: 13.93,
    },

    unitText:{
      marginLeft: wp("30.5%"),
      fontWeight:'bold',
      fontFamily:"Poppins-Regular",
      fontSize: 18.13 ,
    },

    imageRayonnement:{
      marginLeft: wp("14%"),
      marginTop: hp("4%"),
      marginRight: wp("4%")
    },
  
    graph: {
      //flex: 1,
      justifyContent: "center",
      alignItems: "center",
      marginLeft: wp("7%"),
      marginTop: hp("13%")
      //backgroundColor: "#f5fcff"
    },

    footer:{
      flex: 1,
      flexDirection: 'row'
    },

