import React,{ StatusBar, useContext,useEffect,useState} from 'react'
import { View, Text,  StyleSheet, ScrollView, TouchableOpacity,ImageBackground,Image,Dimensions,ActivityIndicator} from 'react-native';
import Search from './Search';
import Menu1 from './Menu1'
import Dashboard from './Dashboard'
import Base from './Base'
import ButtomVoir from './ButtomVoir'
import ProgressBar from "./ProgressBar";
import {Context as AuthContext} from '../context/AuthContext';
import Header from './Header';
import Footer from './Footer';
import DateA from './DateA' ;
import { VictoryBar,VictoryAxis,VictoryVoronoiContainer ,VictoryZoomContainer, VictoryChart, VictoryTheme,VictoryLine,VictoryArea,VictoryClipContainer } from "victory-native";
import ProgressBarr from "./ProgressBarr";
import {
  widthPercentageToDP as wp,
  heightPercentageToDP as hp,
  listenOrientationChange as loc,
  removeOrientationListener as rol
} from 'react-native-responsive-screen';
import { ScaledSheet } from 'react-native-size-matters';
import { s, vs, ms, mvs } from 'react-native-size-matters';

console.disableYellowBox = true;

const EvatranspirationGraph=(props) =>{
  function float2int (value) {
    return value | 0;
  }
  
  const {state,signin} =useContext(AuthContext);
  const [currentDate, setCurrentDate] = useState('');
  const [currentMonth, setCurrentMonth] = useState('');
      useEffect(() => {
          setCurrentDate(
              new Date().getDate()
          );
          setCurrentMonth(
              new Date().getMonth()+1
          );     
        }, []);

    let newDay1;
    let newMonth1;
    let newDate1;
    
    if((currentDate - 5) <= 0){
        if( ((currentMonth - 1) == 2) || ((currentMonth - 1) == 4) || ((currentMonth -1) == 6) || ((currentMonth -1) == 9) || ((currentMonth -1) == 11)){
            newMonth1 = (currentMonth -1);
            if((currentMonth - 1) == 2){
              if((currentDate - 5) == 0)
              newDay1 = 28;
              else
              newDay1 = 28 + (currentDate - 5)
            } else 
              if((currentDate - 5) == 0)
                newDay1 = 30;
              else
                newDay1 = 30 + (currentDate - 5)
        } else
            if((currentDate - 5) == 0)
              newDay1 = 31;
            else
              newDay1 = 31 + (currentDate - 5) 
            if(currentMonth == 1)
              newMonth1 = 12;
            else
              newMonth1 = (currentMonth - 1);
    } else {
        newDay1 = (currentDate - 5);
        newMonth1 = currentMonth;
    } 
    
    if(newDay1 < 10 ){
      if(newMonth1 < 10 )
        newDate1 = '0' + (newDay1) + '/' + '0' + newMonth1;
      else
        newDate1 = '0' + (newDay1) + '/' + newMonth1;
    } else {
      if(newMonth1 < 10 )
        newDate1 = (newDay1) + '/' + '0' + newMonth1;
      else
        newDate1 = (newDay1) + '/' + newMonth1;
    }
  
  let newDay2;
  let newMonth2;
  let newDate2;
  
  if((currentDate - 4) <= 0){
      if( ((currentMonth - 1) == 2) || ((currentMonth - 1) == 4) || ((currentMonth -1) == 6) || ((currentMonth -1) == 9) || ((currentMonth -1) == 11)){
          newMonth2 = (currentMonth -1);
          if((currentMonth - 1)== 2){
            if((currentDate - 4) == 0)
            newDay2 = 28;
            else
            newDay2 = 28 + (currentDate - 4)
          } else 
            if((currentDate - 4) == 0)
              newDay2 = 30;
            else
              newDay2 = 30 + (currentDate - 4)
      } else
          if((currentDate - 4) == 0)
            newDay2 = 31;
          else
            newDay2 = 31 + (currentDate - 4) 
          if(currentMonth == 1)
            newMonth2 = 12;
          else
            newMonth2 = (currentMonth - 1);
  } else {
      newDay2 = (currentDate - 4);
      newMonth2 = currentMonth;
  } 
  
  if(newDay2 < 10 ){
    if(newMonth2 < 10 )
      newDate2 = '0' + (newDay2) + '/' + '0' + newMonth2;
    else
      newDate2 = '0' + (newDay2) + '/' + newMonth2;
  } else {
    if(newMonth2 < 10 )
      newDate2 = (newDay2) + '/' + '0' + newMonth2;
