import { StatusBar } from 'expo-status-bar';
import React,{ useState ,useEffect,useContext} from 'react';
import { Animated,ImageBackground,StyleSheet, Text, View ,Image} from 'react-native';
import {LinearGradient} from 'expo-linear-gradient';
import {Context as AuthContext} from '../context/AuthContext';

import Svg, { G, Defs, ClipPath, Rect, Stop, Line,Polyline, Animate, Polygon } from 'react-native-svg';
console.disableYellowBox = true;


//import Animated, { Easing } from 'react-native-reanimated';

const Progress=({step, steps, height,colorb}) =>{

    const animatedValue=React.useRef(new Animated.Value(-1000)).current;
    const reactive=React.useRef(new Animated.Value(-1000)).current;
    const [width, setWidth]=React.useState(0);
    React.useEffect(() =>{
        Animated.timing(animatedValue, {
          toValue:reactive,
          duration:500,
          //easing:Easing.bounce(),
          useNativeDriver: true,
        }).start();
    }, []);
    React.useEffect(() => {
        reactive.setValue(-width +(width*step)/steps+(width*8)/steps);
    }, [step, width]);
    
    return ( 
    <View > 
      <View style={styles.txt}>
        <Text  style={styles.text}>{step}°C</Text>
      </View>
    <View 
      onLayout={(e) =>{
        const newWidth =e.nativeEvent.layout.width;
        setWidth(newWidth);
      }}
      style={{
        zIndex:1,
        position:'relative',
        width:380,
        marginLeft:"-3%", //-65
        height,
        backgroundColor:'#EDECEC',
        borderRadius: height,
        overflow:"hidden",
        }}
      >
        <Animated.View  
          style={{
            height,
            width:'100%',
            backgroundColor: 'transparent',
            borderRadius: height,
            borderTopRightRadius:0,
            borderBottomRightRadius:0,
            //position:'absolute',
            left:0,
            top:0,
            right:3,
            transform:[{translateX:animatedValue,  /*animatedValue*/ },],
          }}
        >
          <LinearGradient  /*Animated.View*/ colors={colorb}>
            <Text style={{height:70,marginRight:"2345%"}}></Text>
          </LinearGradient>
        </Animated.View>
      </View>
    </View>
  )
}

const ProgressBar=(props) => {
  const {state,signin} =useContext(AuthContext);

  return (
      <View style={styles.container}>
        <Image
          style={{
            zIndex:3,
            position:'absolute',
            marginLeft:"10%",
            width:"73%"
          }}
          source={require('../images/bars.png')}
        />
        <View style={styles.transform} >
          <StatusBar hidden />
          <Progress 
            step={props.dataMin}  steps={67} 
            height={60} colorb={['#27AAE1','#F7941D']}
          />
          <Text></Text>
          <Progress 
            step={props.dataMoy}  steps={67} 
            height={60} colorb={['#ffc500','#F15A29']}
          />
          <Text></Text>
          <Progress 
            step={props.dataMax}  steps={67} 
            height={60} colorb={['#F15A29','#BE1E2D']}
          />
        </View>
      </View>
  );
};

export default ProgressBar;

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    marginTop:-80
  },

  containerr:{
    flex: 1,
    height:'60%',
  },

  txt:{
    marginTop:22,
    marginLeft:320,
    position:'absolute',
    zIndex:2
  },

  transform:{
    transform: [
      { rotate: "-90deg" }, 
    ],
    marginTop:50
  },

  text:{
    transform: [
      { rotate: "90deg" }, 
    ],
    fontSize:14.5,
    fontWeight:'bold',
    color:'transparent'
},
});
