    return value | 0;
  }
  const [currentDate, setCurrentDate] = useState('');
  const [currentMonth, setCurrentMonth] = useState('');
  useEffect(() => {
    setCurrentDate(
      new Date().getDate()
    );
    setCurrentMonth(
      new Date().getMonth()+1
    );      
  }, []);
