import React,{Component,useState,useContext} from 'react';
import CountDown from 'react-native-countdown-component';
import { 
    View, 
    Text, 
    TouchableOpacity, 
    TextInput,
    Platform,
    StyleSheet ,
    Dimensions,
    StatusBar,
    Alert,
    Image
} from 'react-native';
import { Context as AuthContext } from '../context/AuthContext';
import * as Animatable from 'react-native-animatable';
import {LinearGradient} from 'expo-linear-gradient';
import FontAwesome from 'react-native-vector-icons/FontAwesome';
import MaterialIcons from 'react-native-vector-icons/MaterialIcons';
import Feather from 'react-native-vector-icons/Feather';
import Langue2 from './Langue2';
import { set } from 'react-native-reanimated';

console.disableYellowBox = true;

const CountD=(props) => {
    const[results,setResults]=React.useState([]);
    const {state,signin} =useContext(AuthContext);
    const [data, setData] = React.useState({
        password: '',
        check_textInputChange: false,
        secureTextEntry: true,
        isValidUser: true,
        isValidPassword: true,
    });
    const handlePasswordChange = (val) => {
        if( val.trim().length >= 8 ) {
            setData({
                ...data,
                password: val,
                isValidPassword: true
            });
        } else {
            setData({
                ...data,
                password: val,
                isValidPassword: false
            });
        }
    }

    const updateSecureTextEntry = () => {
        setData({
            ...data,
            secureTextEntry: !data.secureTextEntry
        });
    }
    function finished(){
        return(
            <TouchableOpacity
                onPress={()=>props.navigation.navigate('MdpOUblie')}
                style={[styles.signIn, {
                   width:'100%',
                   marginTop: 15,
                }]}
            >
                <LinearGradient
                    colors={['#DBF0F7', '#DBF0F7']}
                    style={styles.signIn}
                >
                    <Text style={[styles.textSign, {color: '#118DCD'}]}>Reenvoyer le code</Text>
                </LinearGradient>
            </TouchableOpacity>
        );
    }
      
    return (
        <View style={styles.container}>
            <Image
                style={styles.header}
                source={require('../images/green.png')}
                resizeMode="stretch"
            />
            <Langue2 />  
            <LinearGradient
                colors={['#fff', '#fff']}
                style={[styles.signIn, {
                    borderColor: '#7FD0D7',
                    borderWidth: 2,
                    marginTop: "20%",
                    borderRadius: 50,
                    marginBottom:15  
                }]}
            >
                <View style={styles.action}>
                    <Image
                        style={{marginLeft:15}}
                        source={require('../images/lock.png')}
                    />
                    <TextInput 
                        placeholder="Entrez votre mot de passe"
                        placeholderTextColor="#7FD0D7"
                        secureTextEntry={data.secureTextEntry ? true : false}
                        value={data.password}
                        onChangeText={(valo) => handlePasswordChange(valo)}
                        style={styles.textInput}
                        autoCapitalize="none"  
                    />
                    <TouchableOpacity
                        style={{marginRight:20}}
                        onPress={updateSecureTextEntry}
                    >
                        {data.secureTextEntry ? 
                        <Image style={{marginLeft:15}} source={require('../images/eye.png')}/>
                        :
                        <Image source={require('../images/eye.png')}/>
                        }
                    </TouchableOpacity>
                </View>
            </LinearGradient>
            <CountDown
                until={60 * 2}
                size={18}
                onFinish={()=>alert('Temps dépassé!Un nouveau code sera envoyé')}
                digitStyle={{backgroundColor: '#FFF', borderWidth: 2, borderColor: '#1CC625'}}
                digitTxtStyle={{color: '#1CC625'}}
                timeToShow={['M', 'S']}
                timeLabels={{m: null, s: null}}
                showSeparator
                separatorStyle={{color: '#1CC625'}}
            />
            <TouchableOpacity
                onPress={()=>props.navigation.navigate('Dashboard')}
                style={[styles.signIn,{
                    width:'100%',
                    marginTop:"6%"
                }]}
                
            >
                <LinearGradient
                    colors={['#118DCD', '#118DCD']}
                    style={styles.signIn}
                >
                    <Text style={[styles.textSign, { color:'#fff'}]}>Valider</Text>
                </LinearGradient>
            </TouchableOpacity>
            <Image
                style={styles.ciel}
                source={require('../images/ciel.png')}
                resizeMode="stretch"
            />
        </View>
    );
};

export default CountD;

const {height} = Dimensions.get("screen");

const styles = StyleSheet.create({
    container: {
        flex: 1, 
        backgroundColor: '#fff',
        alignItems:'center',
        //marginTop:30,
        //height,
    },

    containerr:{
        flex:1,
    },

    header: {
        marginLeft:20,
    },

    footer: {
        flex: 1,
        backgroundColor: '#fff',
        borderTopLeftRadius: 30,
        borderTopRightRadius: 30,
        paddingHorizontal: 20,
        paddingVertical: 30,
        alignItems:'center',
    },

    ciel:{
        marginRight:20,
        marginTop:"20.9%"
    },
    
    text_header: {
        color: '#05375a',
        fontWeight: 'bold',
        fontSize: 30,
    },

    text_footer: {
        color: '#05375a',
        fontSize: 18,
    },

    action: {
        flexDirection: 'row',
        marginTop: 10,
        borderBottomWidth: 1,
        borderBottomColor: '#f2f2f2',
        paddingBottom: 5,
    },

    actionError: {
        flexDirection: 'row',
        marginTop: 10,
        borderBottomWidth: 1,
        borderBottomColor: '#FF0000',
        paddingBottom: 5
    },

    textInput: {
        flex: 1,
        marginTop: Platform.OS === 'ios' ? 0 : -12,
        paddingLeft: 10,
        color: '#05375a',
        //borderBottomColor: 'transparent',
    },

    errorMsg: {
        color: '#FF0000',
        fontSize: 14,
    },
    
    button: {
        alignItems: 'center',
        marginTop: 10
    },

    signIn: {
        width: '80%',
        height: 50,
        justifyContent: 'center',
        alignItems: 'center',
        borderRadius: 10,      
    },
    
    textSign: {
        fontSize: 18,
        fontWeight: 'bold'
    },  
}
);
