import { StatusBar } from 'expo-status-bar';
import React from 'react';
import { Animated,ImageBackground,StyleSheet, Text, View ,Image} from 'react-native';
//import Animated, { Easing } from 'react-native-reanimated';

const Progress=({step, steps, height,colorb}) =>{
    const animatedValue=React.useRef(new Animated.Value(-1000)).current;
    const reactive=React.useRef(new Animated.Value(-1000)).current;
    const [width, setWidth]=React.useState(0);
    React.useEffect(() =>{
        Animated.timing(animatedValue, {
          toValue:reactive,
          duration:400,
          
          //easing:Easing.bounce(),
          useNativeDriver: true,

        }).start();
    }, []);
    React.useEffect(() => {
        reactive.setValue(-width +(width*step)/steps);
    }, [step, width]);
    

    return ( 
   
    <View   
     onLayout={(e) =>{
    const newWidth =e.nativeEvent.layout.width;
    setWidth(newWidth);
    }}
    style={{
     
      position:'absolute',
      width:"100%",
     marginLeft:"-4.6%", //-65
        height:70,
        backgroundColor:'#F2EAE4',
       // borderRadius: height,
        overflow:"hidden",
        
    }}>    
 
  <ImageBackground
   
    source={require('../images/tub.png')}
    style={{
     
      position:'absolute'}}
   >
             </ImageBackground> 

      <Image   source={require('../images/bare.png')}
    style={{
      height:9,
     marginTop:"0.7%",
      zIndex:2,
      position:'absolute',
     }}/>
       
    
<Animated.View  
        
         style={{
          height:70,
         width:'100%',
         backgroundColor: '#C2B49A',
         //borderRadius: height,
         zIndex:1,
         position:'absolute',
          //borderTopRightRadius:0,
         //borderBottomRightRadius:0,
         //position:'absolute',
        //marginLeft :100,
         //top:27,
         transform:[
             {translateX:animatedValue,  //animatedValue
             },
             
         ],
         }}>
          
           </Animated.View>
  
           </View>

        

           


      

)}
const ProgressBarr=(props) => {

  return (
    <View style={styles.container}>
       
        
            <View style={styles.transform} >
            <View>
                <Image   source={require('../images/rectangle.png')} style={styles.transformm} />
                </View>   
      <Progress 
      step={props.dataa}  steps={15} 
      height={0} 
     
      />
      
      </View>
     
         
    </View>
    
    
  );
}


;
export default ProgressBarr;

const styles = StyleSheet.create({
  container: {
   
    flex: 1,
    justifyContent: 'center',
    marginTop:"2.9%",
    
    
  },
  containerr:{
    flex: 1,
    height:'50%',

  },
  transformm:{
    transform: [
        { rotate: "0deg" }, 
      
      ],
      marginTop:"-10.9%",marginLeft:"95%"
  },
 
  transform:{
    transform: [
        { rotate: "-90deg" }, 
      
      ],
      marginTop:"12.4%"
  },
  text:{transform: [
    { rotate: "90deg" }, 
  
  ],
  fontSize:18.5,fontWeight:'bold',color:'black',marginTop:"30%",marginRight:"90%"

},
});
