},
ImageB:{
  marginLeft:"10%",
  marginTop:"4%",
  marginRight:"4%"
},
 text: {
     
 },
 temView2: {
  flexDirection: 'row',
  marginTop:0,
   },
  
   temView3: {
     flexDirection: 'row',
     
   },
   temText: {
     
     fontSize:16,
     textAlign:"center",
     color:'#FFFFFF',
     marginBottom:10,
     fontFamily:'adobe-arabe',
   },
  
   temText4: {
    
     fontSize:17,
     color:'#FFFFFF',
     margin:5,
     marginLeft:20,
     fontFamily:'OpenSans-Regular'

   },
   temText1: {
    
    fontSize:30,
    color:'#FFFFFF',
    margin:10,
    marginBottom:0,
    marginLeft:20,
    fontFamily:'OpenSans-Bold',fontWeight:'bold'

   },
   temText5: {   
    marginLeft:"5%",
    marginRight:"2.4%",
    marginTop:"20%"
   },
   temText6: {   
     marginLeft:60,
     marginTop:10
    
    },
    temText7: {   
     marginLeft:"30%",
     marginTop:"22%",
     height:86
    
    },
    signIn2: {
      width: 180,
      height: 40,
      justifyContent: 'center',
      alignItems: 'center',
      borderRadius: 14, //50
      flexDirection: 'row',
      
    },
    textSign: {
      color: 'white',
      fontWeight: 'bold',
      fontSize:19
    },
 container: {
     flex:1,  
    
 },
 container2: {
backgroundColor:"#F8FAFC" 
 
},
 Image:{
   marginLeft:60,
   marginTop:30
  
 },
 temText8: {
    
   fontSize:20,
   color:'#F15C22',
   
  textAlign:'center'

 },
 image1:{
   marginLeft:9,
