import React,{useContext,useState,useEffect} from 'react'
import { View, Text,  StyleSheet, ScrollView, ImageBackground,Image,Dimensions} from 'react-native';
import Search from './Search';
import Pluie from './Pluie';
import Header from './Header';
import Footer from './Footer';
import {Context as AuthContext} from '../context/AuthContext';
import DateA from './DateA' ;
import Modell from './Modell';
import ButtomVoir from './ButtomVoir'
import { TouchableOpacity } from 'react-native-gesture-handler';
import { VictoryBar,VictoryAxis, VictoryChart, VictoryTheme,VictoryArea,VictoryClipContainer } from "victory-native";
import {
  widthPercentageToDP as wp,
  heightPercentageToDP as hp,
  listenOrientationChange as loc,
  removeOrientationListener as rol
} from 'react-native-responsive-screen';
import { ScaledSheet } from 'react-native-size-matters';
import { s, vs, ms, mvs } from 'react-native-size-matters';
import precipitation from './precipitation';

const PrecipitationGraphe=(props) =>{
  const {width, height} = Dimensions.get('window')
  const {state,signin} =useContext(AuthContext);
  const [currentDate, setCurrentDate] = useState('');
  const [currentMonth, setCurrentMonth] = useState('');
    const alldevice=(a)=>{
      if (a!=1000)
      {
        setStationn({
          pre:state.reponse['devices'][a]['data'][0],
          nom:state.reponse['devices'][a]['station'],
        });
      }
    };
    const[ stationn,setStationn]=useState(
      {
        pre:state.reponse['data infomation'],
        nom:state.reponse['device infomation'].station,
      }
    );
    useEffect(() => {
      setCurrentDate(
        new Date().getDate()
      );
      setCurrentMonth(
        new Date().getMonth()+1
      );
    }, []);
    let newDay1;
    let newMonth1;
    let newDate1;
    
    if((currentDate - 5) <= 0){
        if( ((currentMonth - 1) == 2) || ((currentMonth - 1) == 4) || ((currentMonth -1) == 6) || ((currentMonth -1) == 9) || ((currentMonth -1) == 11)){
            newMonth1 = (currentMonth -1);
            if((currentMonth - 1) == 2){
              if((currentDate - 5) == 0)
              newDay1 = 28;
              else
              newDay1 = 28 + (currentDate - 5)
            } else 
              if((currentDate - 5) == 0)
                newDay1 = 30;
              else
                newDay1 = 30 + (currentDate - 5)
        } else
            if((currentDate - 5) == 0)
              newDay1 = 31;
            else
              newDay1 = 31 + (currentDate - 5) 
            if(currentMonth == 1)
              newMonth1 = 12;
            else
              newMonth1 = (currentMonth - 1);
    } else {
        newDay1 = (currentDate - 5);
        newMonth1 = currentMonth;
    } 
    
    if(newDay1 < 10 ){
      if(newMonth1 < 10 )
        newDate1 = '0' + (newDay1) + '/' + '0' + newMonth1;
      else
        newDate1 = '0' + (newDay1) + '/' + newMonth1;
    } else {
      if(newMonth1 < 10 )
        newDate1 = (newDay1) + '/' + '0' + newMonth1;
      else
        newDate1 = (newDay1) + '/' + newMonth1;
    }
  
  let newDay2;
  let newMonth2;
  let newDate2;
  
  if((currentDate - 4) <= 0){
